const BookmarkModal = ({ 
  isOpen, 
  onClose, 
  onSave, 
  bookmark = null, 
  folders = [] 
}) => {
  const [formData, setFormData] = useState({
    name: '',
    url: '',
    icon: '',
    folderId: ''
  });
  
  const [iconType, setIconType] = useState('favicon'); // favicon, emoji, image
  const [selectedEmoji, setSelectedEmoji] = useState('üîñ');
  const [imagePreview, setImagePreview] = useState('');
  const fileInputRef = useRef(null);
  
  // ÂàùÂßãÂåñË°®ÂçïÊï∞ÊçÆ
  useEffect(() => {
    if (bookmark) {
      setFormData({
        name: bookmark.name,
        url: bookmark.url,
        icon: bookmark.icon,
        folderId: bookmark.folderId || ''
      });
      
      // Á°ÆÂÆöÂõæÊ†áÁ±ªÂûã
      if (bookmark.icon.startsWith('data:image') || bookmark.icon.startsWith('http') || bookmark.icon.startsWith('file:')) {
        setIconType('image');
        setImagePreview(bookmark.icon);
      } else if (bookmark.icon.length <= 2) {
        setIconType('emoji');
        setSelectedEmoji(bookmark.icon);
      } else {
        setIconType('favicon');
      }
    } else {
      setFormData({
        name: '',
        url: '',
        icon: '',
        folderId: ''
      });
      setIconType('favicon');
      setSelectedEmoji('üîñ');
      setImagePreview('');
    }
  }, [bookmark, isOpen]);
  
  // Â§ÑÁêÜË°®ÂçïËæìÂÖ•ÂèòÂåñ
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };
  
  // Â§ÑÁêÜURLÂèòÂåñÔºåËá™Âä®Ëé∑ÂèñÁΩëÁ´ôÂõæÊ†á
  const handleUrlChange = (e) => {
    const { value } = e.target;
    setFormData(prev => ({
      ...prev,
      url: value
    }));
    
    if (iconType === 'favicon' && value) {
      const faviconUrl = getFaviconUrl(value);
      setFormData(prev => ({
        ...prev,
        icon: faviconUrl
      }));
    }
  };
  
  // Â§ÑÁêÜÂõæÊ†áÁ±ªÂûãÂèòÂåñ
  const handleIconTypeChange = (type) => {
    setIconType(type);
    
    // Ê†πÊçÆÁ±ªÂûãËÆæÁΩÆÈªòËÆ§ÂõæÊ†á
    if (type === 'emoji') {
      setFormData(prev => ({
        ...prev,
        icon: selectedEmoji
      }));
    } else if (type === 'favicon') {
      if (formData.url) {
        const faviconUrl = getFaviconUrl(formData.url);
        setFormData(prev => ({
          ...prev,
          icon: faviconUrl
        }));
      } else {
        setFormData(prev => ({
          ...prev,
          icon: ''
        }));
      }
    } else if (type === 'image') {
      setFormData(prev => ({
        ...prev,
        icon: imagePreview || ''
      }));
    }
  };
  
  // Â§ÑÁêÜË°®ÊÉÖÁ¨¶Âè∑ÈÄâÊã©
  const handleEmojiSelect = (emoji) => {
    setSelectedEmoji(emoji);
    setFormData(prev => ({
      ...prev,
      icon: emoji
    }));
  };
  
  // Â§ÑÁêÜÂõæÁâáÊñá‰ª∂ÈÄâÊã©
  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (event) => {
        setImagePreview(event.target.result);
        setFormData(prev => ({
          ...prev,
          icon: event.target.result
        }));
      };
      reader.readAsDataURL(file);
    }
  };
  
  // ‰øùÂ≠ò‰π¶Á≠æ
  const handleSave = () => {
    if (!formData.name.trim() || !formData.url.trim()) {
      alert('ËØ∑Â°´ÂÜôÂÆåÊï¥‰ø°ÊÅØ');
      return;
    }
    
    onSave({
      ...formData,
      name: formData.name.trim(),
      url: formData.url.trim(),
      folderId: formData.folderId || null
    });
  };
  
  if (!isOpen) return null;
  
  return (
    <Modal>
      <ModalContent>
        <ModalHeader>
          <ModalTitle>{bookmark ? 'ÁºñËæë‰π¶Á≠æ' : 'Ê∑ªÂä†‰π¶Á≠æ'}</ModalTitle>
          <CloseButton onClick={onClose}>&times;</CloseButton>
        </ModalHeader>
        
        <FormGroup>
          <Label>ÂêçÁß∞</Label>
          <Input 
            type="text" 
            name="name" 
            value={formData.name} 
            onChange={handleInputChange} 
            placeholder="‰π¶Á≠æÂêçÁß∞"
          />
        </FormGroup>
        
        <FormGroup>
          <Label>URL</Label>
          <Input 
            type="text" 
            name="url" 
            value={formData.url} 
            onChange={handleUrlChange} 
            placeholder="https://example.com"
          />
        </FormGroup>
        
        <FormGroup>
          <Label>Êñá‰ª∂Â§π</Label>
          <Select 
            name="folderId" 
            value={formData.folderId} 
            onChange={handleInputChange}
          >
            <option value="">Ê†πÁõÆÂΩï</option>
            {folders.map(folder => (
              <option key={folder.id} value={folder.id}>{folder.name}</option>
            ))}
          </Select>
        </FormGroup>
        
        <FormGroup>
          <Label>ÂõæÊ†á</Label>
          <IconOptions>
            <IconOption 
              active={iconType === 'favicon'} 
              onClick={() => handleIconTypeChange('favicon')}
            >
              <LinkIcon />
            </IconOption>
            <IconOption 
              active={iconType === 'emoji'} 
              onClick={() => handleIconTypeChange('emoji')}
            >
              <EmojiEmotionsIcon />
            </IconOption>
            <IconOption 
              active={iconType === 'image'} 
              onClick={() => handleIconTypeChange('image')}
            >
              <ImageIcon />
            </IconOption>
          </IconOptions>
          
          {iconType === 'emoji' && (
            <EmojiPicker>
              {commonEmojis.map(emoji => (
                <EmojiButton 
                  key={emoji} 
                  onClick={() => handleEmojiSelect(emoji)}
                  style={{ 
                    backgroundColor: selectedEmoji === emoji ? 'rgba(0, 0, 0, 0.1)' : 'transparent' 
                  }}
                >
                  {emoji}
                </EmojiButton>
              ))}
            </EmojiPicker>
          )}
          
          {iconType === 'image' && (
            <>
              <FileInputLabel>
                ÈÄâÊã©ÂõæÁâá
                <FileInput 
                  type="file" 
                  accept="image/*" 
                  ref={fileInputRef}
                  onChange={handleFileChange}
                />
              </FileInputLabel>
              
              {imagePreview && (
                <ImagePreview>
                  <img src={imagePreview} alt="È¢ÑËßà" />
                </ImagePreview>
              )}
            </>
          )}
        </FormGroup>
        
        <ButtonGroup>
          <Button className="secondary" onClick={onClose}>ÂèñÊ∂à</Button>
          <Button className="primary" onClick={handleSave}>‰øùÂ≠ò</Button>
        </ButtonGroup>
      </ModalContent>
    </Modal>
  );
};

export default BookmarkModal;
