import React, { useState, useEffect, useCallback } from 'react';
import styled from 'styled-components';
import { SettingsProvider } from './contexts/SettingsContext';
import { ThemeProvider } from './contexts/ThemeContext';
import appInitializer from './utils/appInitializer';
import configManager from './utils/simpleConfigManager';

// ÁªÑ‰ª∂ÂØºÂÖ•
import Header from './components/Header/Header';
import Search from './components/Search/Search';
import ModuleSwitch from './components/ModuleSwitch/ModuleSwitch';
import EnhancedSettingsPanel from './settings/EnhancedSettingsPanel';
import Loading from './components/Loading/Loading';

// Ê®°ÂùóÂØºÂÖ•
import Calendar from './modules/Calendar/Calendar';
import Bookmark from './modules/Bookmark/Bookmark';
import Todo from './modules/Todo/Todo';
import Note from './modules/Note/Note';
import Quote from './modules/Quote/Quote';
import News from './modules/News/News';

// Ê®°ÂºèÂØºÂÖ•
import ZenMode from './modes/ZenMode/ZenMode';
import CurtainMode from './modes/CurtainMode/CurtainMode';

const AppContainer = styled.div`
  min-height: 100vh;
  background: var(--background-color, #ffffff);
  color: var(--text-color, #333333);
  font-family: var(--font-family, 'Microsoft YaHei', sans-serif);
  transition: all 0.3s ease;
  position: relative;
  overflow-x: hidden;
`;

const MainContent = styled.main`
  padding: 20px;
  max-width: 1200px;
  margin: 0 auto;
  min-height: calc(100vh - 80px);
  display: flex;
  flex-direction: column;
  gap: 20px;
`;


const ErrorContainer = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  min-height: 100vh;
  background: var(--background-color, #ffffff);
  color: var(--text-color, #333333);
  text-align: center;
  padding: 20px;
`;

const ErrorContent = styled.div`
  max-width: 500px;
  
  h2 {
    color: #e74c3c;
    margin-bottom: 15px;
  }
  
  p {
    margin-bottom: 20px;
    line-height: 1.6;
  }
  
  button {
    background: var(--primary-color, #4a90e2);
    color: white;
    border: none;
    padding: 10px 20px;
    border-radius: 5px;
    cursor: pointer;
    font-size: 16px;
    
    &:hover {
      opacity: 0.9;
    }
  }
`;

// Ê®°ÂùóÊò†Â∞Ñ
const moduleComponents = {
  calendar: Calendar,
  bookmark: Bookmark,
  todo: Todo,
  note: Note,
  quote: Quote,
  news: News
};

function App() {
  const [currentModule, setCurrentModule] = useState('quote');
  const [isSettingsOpen, setIsSettingsOpen] = useState(false);
  const [currentMode, setCurrentMode] = useState('normal');
  const [isInitialized, setIsInitialized] = useState(false);
  const [error, setError] = useState(null);
  const [isLoading, setIsLoading] = useState(true);

  // ÈîôËØØËæπÁïåÂ§ÑÁêÜ
  const handleError = useCallback((error, errorInfo) => {
    console.error('Â∫îÁî®ÈîôËØØ:', error, errorInfo);
    setError(error);
  }, []);

  // ÂàùÂßãÂåñÂ∫îÁî®
  useEffect(() => {
    const initializeApp = async () => {
      try {
        setIsLoading(true);
        
        // ‰ΩøÁî®Â∫îÁî®ÂêØÂä®ÁÆ°ÁêÜÂô®ÂàùÂßãÂåñ
        const result = await appInitializer.initialize({
          delay: 150, // ÂáèÂ∞ëÂª∂ËøüÔºåÊèêÂçáÂìçÂ∫îÈÄüÂ∫¶
          showLoadingScreen: false // ‰ΩøÁî®Ëá™Â∑±ÁöÑÂä†ËΩΩÁªÑ‰ª∂
        });
        
        if (result.success) {
          setCurrentModule(result.startupModule);
          setIsInitialized(true);
          console.log('‚úÖ Â∫îÁî®ÂàùÂßãÂåñÂÆåÊàê');
          console.log('üì± ÂêØÂä®Ê®°Âùó:', result.startupModule);
          console.log('‚öôÔ∏è ËÆæÁΩÆÂ∑≤Â∫îÁî®:', Object.keys(result.settings).length, 'È°πÈÖçÁΩÆ');
        } else {
          console.error('‚ùå Â∫îÁî®ÂàùÂßãÂåñÂ§±Ë¥•:', result.error);
          handleError(result.error || new Error(result.message));
          // Âç≥‰ΩøÂ§±Ë¥•‰πüËÆæÁΩÆÈªòËÆ§Áä∂ÊÄÅ
          setCurrentModule('quote');
          setIsInitialized(true);
        }
        
      } catch (error) {
        console.error('üí• ÂàùÂßãÂåñÂ∫îÁî®Êó∂Âá∫Èîô:', error);
        handleError(error);
        // ËÆæÁΩÆÈªòËÆ§Áä∂ÊÄÅ
        setCurrentModule('quote');
        setIsInitialized(true);
      } finally {
        setIsLoading(false);
      }
    };

    initializeApp();
  }, [handleError]);

  // Â§ÑÁêÜÊ®°ÂùóÂàáÊç¢
  const handleModuleChange = useCallback((module) => {
    try {
      if (moduleComponents[module]) {
        setCurrentModule(module);
        // ‰øùÂ≠òÂΩìÂâçÊ®°Âùó‰∏∫‰∏äÊ¨°‰ΩøÁî®ÁöÑÊ®°Âùó
        configManager.set('data.lastActiveModule', module);
      }
    } catch (error) {
      console.error('ÂàáÊç¢Ê®°ÂùóÊó∂Âá∫Èîô:', error);
      handleError(error);
    }
  }, [handleError]);

  // Â§ÑÁêÜËÆæÁΩÆÊõ¥Êñ∞
  const handleSettingsUpdate = useCallback((newSettings) => {
    try {
      // ‰ΩøÁî®Â∫îÁî®ÂêØÂä®ÁÆ°ÁêÜÂô®Êõ¥Êñ∞ËÆæÁΩÆ
      appInitializer.updateSettings(newSettings);
    } catch (error) {
      console.error('Êõ¥Êñ∞ËÆæÁΩÆÊó∂Âá∫Èîô:', error);
    }
  }, []);

  // Â§ÑÁêÜÊ®°ÂºèÂàáÊç¢
  const handleModeChange = useCallback((mode) => {
    try {
      setCurrentMode(mode);
    } catch (error) {
      console.error('ÂàáÊç¢Ê®°ÂºèÊó∂Âá∫Èîô:', error);
      handleError(error);
    }
  }, [handleError]);

  // Ê∏≤ÊüìÂΩìÂâçÊ®°Âùó
  const renderCurrentModule = useCallback(() => {
    try {
      const ModuleComponent = moduleComponents[currentModule];
      if (!ModuleComponent) {
        return <div>Ê®°Âùó‰∏çÂ≠òÂú®: {currentModule}</div>;
      }
      return <ModuleComponent />;
    } catch (error) {
      console.error('Ê∏≤ÊüìÊ®°ÂùóÊó∂Âá∫Èîô:', error);
      return <div>Ê®°ÂùóÂä†ËΩΩÂ§±Ë¥•ÔºåËØ∑Âà∑Êñ∞È°µÈù¢ÈáçËØï</div>;
    }
  }, [currentModule]);

  // ÈîôËØØÁä∂ÊÄÅ
  if (error) {
    return (
      <ErrorContainer>
        <ErrorContent>
          <h2>Â∫îÁî®Âá∫Áé∞ÈîôËØØ</h2>
          <p>Êä±Ê≠âÔºåÂ∫îÁî®ÈÅáÂà∞‰∫Ü‰∏Ä‰∏™ÈîôËØØ„ÄÇËØ∑Â∞ùËØïÂà∑Êñ∞È°µÈù¢„ÄÇ</p>
          <p style={{ fontSize: '14px', color: 'var(--text-secondary-color)' }}>
            ÈîôËØØ‰ø°ÊÅØ: {error.message}
          </p>
          <button onClick={() => window.location.reload()}>
            Âà∑Êñ∞È°µÈù¢
          </button>
        </ErrorContent>
      </ErrorContainer>
    );
  }

  // Âä†ËΩΩÁä∂ÊÄÅ
  if (isLoading || !isInitialized) {
    return <Loading text="Ê≠£Âú®Âä†ËΩΩ..." subtext="ËØ∑Á®çÂÄôÔºåÊ≠£Âú®ÂàùÂßãÂåñÂ∫îÁî®Á®ãÂ∫è" />;
  }

  // ÁâπÊÆäÊ®°ÂºèÊ∏≤Êüì
  if (currentMode === 'zen') {
    return (
      <ThemeProvider>
        <SettingsProvider>
          <ZenMode onBack={() => handleModeChange('normal')} />
        </SettingsProvider>
      </ThemeProvider>
    );
  }

  if (currentMode === 'curtain') {
    return (
      <ThemeProvider>
        <SettingsProvider>
          <CurtainMode onBack={() => handleModeChange('normal')} />
        </SettingsProvider>
      </ThemeProvider>
    );
  }

  // Ê≠£Â∏∏Ê®°ÂºèÊ∏≤Êüì
  return (
    <ThemeProvider>
      <SettingsProvider>
        <AppContainer>
          <Header 
            onSettingsClick={() => setIsSettingsOpen(true)}
            onModeChange={handleModeChange}
            currentMode={currentMode}
            showBackButton={false}
            onBackClick={() => {}}
          />
          
          <MainContent>
            <Search />
            
            <ModuleSwitch 
              activeModule={currentModule}
              onModuleChange={handleModuleChange}
            />
            
            {renderCurrentModule()}
          </MainContent>
          
        <EnhancedSettingsPanel 
          isOpen={isSettingsOpen}
          onClose={() => setIsSettingsOpen(false)}
          onSettingsUpdate={handleSettingsUpdate}
        />
        </AppContainer>
      </SettingsProvider>
    </ThemeProvider>
  );
}

export default App;
