import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { saveToStorage, getFromStorage } from '../../utils/storage';
import axios from 'axios';
import xml2js from 'xml2js';

// 图标导入
import AddIcon from '@mui/icons-material/Add';
import EditIcon from '@mui/icons-material/Edit';
import DeleteIcon from '@mui/icons-material/Delete';
import ArrowBackIcon from '@mui/icons-material/ArrowBack';
import RssFeedIcon from '@mui/icons-material/RssFeed';
import LinkIcon from '@mui/icons-material/Link';

const NewsContainer = styled.div`
  width: 100%;
  height: 100vh;
  display: flex;
  flex-direction: column;
`;

const NewsHeader = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 15px 20px;
  background-color: var(--card-background);
  box-shadow: 0 2px 5px var(--shadow-color);
`;

const HeaderTitle = styled.h2`
  margin: 0;
  font-size: 20px;
  font-weight: 500;
`;

const BackButton = styled.button`
  background-color: var(--primary-color);
  color: white;
  border: none;
  border-radius: 50%;
  width: 36px;
  height: 36px;
  display: flex;
  align-items: center;
  justify-content: center;
  cursor: pointer;
  margin-right: 15px;
  
  &:hover {
    background-color: #3a7bc8;
  }
`;

const SourcesContainer = styled.div`
  display: flex;
  align-items: center;
  overflow-x: auto;
  padding: 10px 20px;
  background-color: var(--background-color);
  border-bottom: 1px solid var(--border-color);
  
  &::-webkit-scrollbar {
    height: 4px;
  }
  
  &::-webkit-scrollbar-track {
    background: var(--background-color);
  }
  
  &::-webkit-scrollbar-thumb {
    background-color: var(--border-color);
    border-radius: 4px;
  }
`;

const SourceButton = styled.button`
  background-color: ${props => props.active ? 'var(--primary-color)' : 'var(--card-background)'};
  color: ${props => props.active ? 'white' : 'var(--text-color)'};
  border: none;
  border-radius: 20px;
  padding: 8px 15px;
  margin-right: 10px;
  cursor: pointer;
  white-space: nowrap;
  display: flex;
  align-items: center;
  gap: 5px;
  
  &:hover {
    background-color: ${props => props.active ? 'var(--primary-color)' : 'rgba(0, 0, 0, 0.05)'};
  }
  
  svg {
    font-size: 16px;
  }
`;

const AddSourceButton = styled.button`
  background-color: var(--card-background);
  color: var(--primary-color);
  border: 1px dashed var(--primary-color);
  border-radius: 20px;
  padding: 8px 15px;
  margin-right: 10px;
  cursor: pointer;
  white-space: nowrap;
  display: flex;
  align-items: center;
  gap: 5px;
  
  &:hover {
    background-color: rgba(0, 0, 0, 0.05);
  }
`;

const NewsContent = styled.div`
  flex: 1;
  padding: 20px;
  overflow-y: auto;
`;

const NewsList = styled.div`
  display: flex;
  flex-direction: column;
  gap: 15px;
`;

const NewsItem = styled.a`
  display: block;
  padding: 15px;
  background-color: var(--card-background);
  border-radius: 8px;
  box-shadow: 0 2px 5px var(--shadow-color);
  cursor: pointer;
  text-decoration: none;
  color: var(--text-color);
  transition: all var(--transition-speed);
  
  &:hover {
    transform: translateY(-3px);
    box-shadow: 0 5px 15px var(--shadow-color);
  }
  
  &:visited {
    opacity: 0.7;
  }
`;

const NewsTitle = styled.h3`
  margin: 0;
  font-size: 16px;
  font-weight: 500;
`;

const NewsDate = styled.div`
  font-size: 12px;
  color: var(--text-secondary-color);
  margin-top: 5px;
`;

const LoadingState = styled.div`
  text-align: center;
  padding: 40px 20px;
  color: var(--text-color);
`;

const ErrorState = styled.div`
  text-align: center;
  padding: 40px 20px;
  color: var(--text-color);
  color: #e53935;
`;

const EmptyState = styled.div`
  text-align: center;
  padding: 40px 20px;
  color: var(--text-color);
  opacity: 0.7;
`;

const Modal = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background-color: rgba(0, 0, 0, 0.5);
  display: flex;
  align-items: center;
  justify-content: center;
  z-index: 1000;
`;

const ModalContent = styled.div`
  background-color: var(--background-color);
  padding: 20px;
  border-radius: 8px;
  width: 90%;
  max-width: 500px;
  box-shadow: 0 5px 15px rgba(0, 0, 0, 0.3);
`;

const ModalHeader = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 20px;
`;

const ModalTitle = styled.h3`
  margin: 0;
  font-size: 18px;
  font-weight: 500;
`;

const CloseButton = styled.button`
  background: none;
  border: none;
  cursor: pointer;
  font-size: 20px;
  color: var(--text-color);
`;

const FormGroup = styled.div`
  margin-bottom: 15px;
`;

const Label = styled.label`
  display: block;
  margin-bottom: 5px;
  font-size: 14px;
`;

const Input = styled.input`
  width: 100%;
  padding: 8px;
  border: 1px solid var(--border-color);
  border-radius: 4px;
  background-color: var(--background-color);
  color: var(--text-color);
`;

const Select = styled.select`
  width: 100%;
  padding: 8px;
  border: 1px solid var(--border-color);
  border-radius: 4px;
  background-color: var(--background-color);
  color: var(--text-color);
`;

const ButtonGroup = styled.div`
  display: flex;
  justify-content: flex-end;
  gap: 10px;
  margin-top: 20px;
`;

const Button = styled.button`
  padding: 8px 16px;
  border: none;
  border-radius: 4px;
  cursor: pointer;
  font-size: 14px;
  
  &.primary {
    background-color: var(--primary-color);
    color: white;
    
    &:hover {
      background-color: #3a7bc8;
    }
  }
  
  &.secondary {
    background-color: var(--card-background);
    color: var(--text-color);
    border: 1px solid var(--border-color);
    
    &:hover {
      background-color: rgba(0, 0, 0, 0.05);
    }
  }
  
  &.danger {
    background-color: #e53935;
    color: white;
    
    &:hover {
      background-color: #c62828;
    }
  }
`;

const SourcesList = styled.div`
  display: flex;
  flex-direction: column;
  gap: 10px;
  margin-top: 20px;
`;

const SourceItem = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 10px;
  background-color: var(--card-background);
  border-radius: 4px;
  border-left: 3px solid ${props => props.type === 'rss' ? '#ff9800' : '#2196f3'};
`;

const SourceInfo = styled.div`
  display: flex;
  align-items: center;
  gap: 10px;
`;

const SourceName = styled.div`
  font-weight: 500;
`;

const SourceUrl = styled.div`
  font-size: 12px;
  color: var(--text-secondary-color);
  word-break: break-all;
`;

const SourceActions = styled.div`
  display: flex;
  gap: 5px;
`;

const ActionIconButton = styled.button`
  background-color: transparent;
  border: none;
  border-radius: 50%;
  width: 30px;
  height: 30px;
  display: flex;
  align-items: center;
  justify-content: center;
  cursor: pointer;
  color: var(--text-secondary-color);
  
  &:hover {
    background-color: rgba(0, 0, 0, 0.05);
    color: var(--primary-color);
  }
  
  &.delete:hover {
    color: #e53935;
  }
`;

// 代理服务器URL，用于解决跨域问题
// 提供多个代理服务器，如果一个失败可以尝试另一个
const CORS_PROXIES = [
  'https://corsproxy.io/?',
  'https://api.allorigins.win/raw?url=',
  'https://cors-anywhere.herokuapp.com/'
];

const News = () => {
  // 状态
  const [sources, setSources] = useState([]);
  const [activeSource, setActiveSource] = useState(null);
  const [newsItems, setNewsItems] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [showSourcesModal, setShowSourcesModal] = useState(false);
  const [editingSource, setEditingSource] = useState(null);
  
  const [formData, setFormData] = useState({
    name: '',
    url: '',
    type: 'rss' // rss 或 html
  });
  
  // 初始化数据
  useEffect(() => {
    const savedSources = getFromStorage('homepage-news-sources', []);
    setSources(savedSources);
    
    if (savedSources.length > 0) {
      setActiveSource(savedSources[0]);
    }
  }, []);
  
  // 当活动源变化时，加载新闻
  useEffect(() => {
    if (activeSource) {
      loadNews(activeSource);
    }
  }, [activeSource]);
  
  // 加载新闻
  const loadNews = async (source) => {
    setLoading(true);
    setError(null);
    setNewsItems([]);
    
    try {
      if (source.type === 'rss') {
        await loadRssNews(source);
      } else {
        await loadHtmlNews(source);
      }
    } catch (err) {
      console.error('加载新闻失败:', err);
      setError(`加载失败: ${err.message}`);
    } finally {
      setLoading(false);
    }
  };
  
  // 加载RSS新闻
  const loadRssNews = async (source) => {
    let lastError = null;
    
    // 尝试所有代理服务器
    for (const proxy of CORS_PROXIES) {
      try {
        console.log(`尝试使用代理: ${proxy} 加载: ${source.url}`);
        
        // 特殊处理知乎RSS
        let targetUrl = source.url;
        let headers = {
          'Accept': 'application/rss+xml, application/xml, text/xml, */*'
        };
        
        // 知乎特殊处理
        if (source.url.includes('zhihu.com')) {
          // 知乎需要特殊的User-Agent
          headers['User-Agent'] = 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/91.0.4472.124 Safari/537.36';
          
          // 如果是知乎主域名，转换为知乎专栏的RSS地址
          if (source.url === 'https://www.zhihu.com/rss' || source.url === 'http://www.zhihu.com/rss') {
            targetUrl = 'https://www.zhihu.com/rss';
          }
        }
        
        // 52pojie特殊处理
        if (source.url.includes('52pojie.cn')) {
          headers['User-Agent'] = 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/91.0.4472.124 Safari/537.36';
          headers['Referer'] = 'https://www.52pojie.cn/';
        }
        
        // 使用代理服务器解决跨域问题
        const response = await axios.get(`${proxy}${encodeURIComponent(targetUrl)}`, { headers });
        const xmlData = response.data;
        
        // 如果返回的是HTML而不是XML，尝试提取RSS链接
        if (typeof xmlData === 'string' && xmlData.includes('<!DOCTYPE html>')) {
          console.log('返回了HTML而不是XML，尝试提取RSS链接');
          
          // 创建一个临时的DOM元素来解析HTML
          const parser = new DOMParser();
          const doc = parser.parseFromString(xmlData, 'text/html');
          
          // 尝试找到RSS链接
          const rssLinks = Array.from(doc.querySelectorAll('link[type="application/rss+xml"], link[type="application/atom+xml"]'));
          
          if (rssLinks.length > 0) {
            const rssUrl = rssLinks[0].getAttribute('href');
            console.log(`找到RSS链接: ${rssUrl}`);
            
            // 处理相对URL
            let absoluteRssUrl = rssUrl;
            if (rssUrl.startsWith('/')) {
              const urlObj = new URL(targetUrl);
              absoluteRssUrl = `${urlObj.protocol}//${urlObj.host}${rssUrl}`;
            }
            
            // 递归调用，使用找到的RSS链接
            const newSource = { ...source, url: absoluteRssUrl };
            return loadRssNews(newSource);
          } else {
            throw new Error('返回的是HTML而不是RSS，且未找到RSS链接');
          }
        }
        
        // 解析XML
        let parsePromise = new Promise((resolve, reject) => {
          xml2js.parseString(xmlData, (err, result) => {
            if (err) {
              console.error('XML解析错误:', err);
              reject(new Error('解析RSS失败'));
              return;
            }
            resolve(result);
          });
        });
        
        const result = await parsePromise;
        let items = [];
        
        try {
          // 处理不同的RSS格式
          if (result.rss && result.rss.channel && result.rss.channel[0].item) {
            // 标准RSS 2.0
            items = result.rss.channel[0].item.map(item => ({
              title: item.title ? (typeof item.title[0] === 'string' ? item.title[0] : item.title[0]._) : '无标题',
              link: item.link ? (typeof item.link[0] === 'string' ? item.link[0] : item.link[0]._) : '#',
              date: item.pubDate ? new Date(item.pubDate[0]) : new Date(),
              description: item.description ? (typeof item.description[0] === 'string' ? item.description[0] : item.description[0]._) : ''
            }));
          } else if (result.feed && result.feed.entry) {
            // Atom格式
            items = result.feed.entry.map(entry => {
              // 处理链接
              let link = '#';
              if (entry.link) {
                if (Array.isArray(entry.link)) {
                  const linkObj = entry.link.find(l => l.$ && (l.$.rel === 'alternate' || !l.$.rel));
                  if (linkObj && linkObj.$ && linkObj.$.href) {
                    link = linkObj.$.href;
                  } else if (entry.link[0] && entry.link[0].$ && entry.link[0].$.href) {
                    link = entry.link[0].$.href;
                  }
                } else if (typeof entry.link === 'string') {
                  link = entry.link;
                }
              }
              
              // 处理标题
              let title = '无标题';
              if (entry.title) {
                if (typeof entry.title[0] === 'string') {
                  title = entry.title[0];
                } else if (entry.title[0] && entry.title[0]._) {
                  title = entry.title[0]._;
                } else if (typeof entry.title === 'string') {
                  title = entry.title;
                }
              }
              
              // 处理日期
              let date = new Date();
              if (entry.updated) {
                date = new Date(typeof entry.updated[0] === 'string' ? entry.updated[0] : entry.updated);
              } else if (entry.published) {
                date = new Date(typeof entry.published[0] === 'string' ? entry.published[0] : entry.published);
              }
              
              // 处理描述
              let description = '';
              if (entry.summary) {
                description = typeof entry.summary[0] === 'string' ? entry.summary[0] : 
                             (entry.summary[0] && entry.summary[0]._ ? entry.summary[0]._ : '');
              } else if (entry.content) {
                description = typeof entry.content[0] === 'string' ? entry.content[0] : 
                             (entry.content[0] && entry.content[0]._ ? entry.content[0]._ : '');
              }
              
              return { title, link, date, description };
            });
          } else if (result['rdf:RDF']) {
            // RDF格式
            const rdfItems = result['rdf:RDF'].item || 
                           (result['rdf:RDF'].channel && result['rdf:RDF'].channel[0] && result['rdf:RDF'].channel[0].item);
            
            if (rdfItems) {
              items = rdfItems.map(item => ({
                title: item.title ? (typeof item.title[0] === 'string' ? item.title[0] : item.title[0]._) : '无标题',
                link: item.link ? (typeof item.link[0] === 'string' ? item.link[0] : item.link[0]._) : '#',
                date: item['dc:date'] ? new Date(item['dc:date'][0]) : new Date(),
                description: item.description ? (typeof item.description[0] === 'string' ? item.description[0] : item.description[0]._) : ''
              }));
            }
          } else {
            // 尝试遍历结果找到可能的项目
            const findItems = (obj, depth = 0) => {
              if (!obj || typeof obj !== 'object' || depth > 5) return null;
              
              // 直接查找item或entry数组
              if (obj.item && Array.isArray(obj.item)) {
                return obj.item.map(item => ({
                  title: extractTextContent(item.title) || '无标题',
                  link: extractTextContent(item.link) || '#',
                  date: item.pubDate ? new Date(extractTextContent(item.pubDate)) : new Date(),
                  description: extractTextContent(item.description) || ''
                }));
              }
              
              if (obj.entry && Array.isArray(obj.entry)) {
                return obj.entry.map(entry => ({
                  title: extractTextContent(entry.title) || '无标题',
                  link: extractLinkFromEntry(entry) || '#',
                  date: entry.updated ? new Date(extractTextContent(entry.updated)) : new Date(),
                  description: extractTextContent(entry.summary || entry.content) || ''
                }));
              }
              
              // 递归查找
              for (const key in obj) {
                if (typeof obj[key] === 'object' && obj[key] !== null) {
                  const found = findItems(obj[key], depth + 1);
                  if (found && found.length > 0) return found;
                }
              }
              
              return null;
            };
            
            // 辅助函数：从各种可能的格式中提取文本内容
            const extractTextContent = (value) => {
              if (!value) return '';
              if (typeof value === 'string') return value;
              if (Array.isArray(value)) {
                if (value.length === 0) return '';
                if (typeof value[0] === 'string') return value[0];
                if (value[0] && value[0]._) return value[0]._;
                if (value[0] && typeof value[0] === 'object') {
                  for (const key in value[0]) {
                    if (typeof value[0][key] === 'string') return value[0][key];
                  }
                }
              }
              return '';
            };
            
            // 辅助函数：从entry对象中提取链接
            const extractLinkFromEntry = (entry) => {
              if (!entry.link) return '';
              if (typeof entry.link === 'string') return entry.link;
              if (Array.isArray(entry.link)) {
                for (const link of entry.link) {
                  if (typeof link === 'string') return link;
                  if (link.$ && link.$.href) return link.$.href;
                }
              }
              return '';
            };
            
            const foundItems = findItems(result);
            if (foundItems && foundItems.length > 0) {
              items = foundItems;
            } else {
              console.error('未找到支持的RSS格式:', result);
              throw new Error('不支持的RSS格式');
            }
          }
        } catch (parseError) {
          console.error('RSS内容解析错误:', parseError, result);
          throw new Error('RSS内容解析失败');
        }
        
        // 成功获取到新闻项，设置状态并返回
        setNewsItems(items);
        return;
        
      } catch (error) {
        console.error(`使用代理 ${proxy} 加载失败:`, error);
        lastError = error;
        // 继续尝试下一个代理
      }
    }
    
    // 所有代理都失败了
    console.error('所有代理服务器都失败了');
    throw lastError || new Error('加载RSS失败: 所有代理服务器都失败了');
  };
  
  // 加载HTML新闻
  const loadHtmlNews = async (source) => {
    try {
      // 使用代理服务器解决跨域问题
      const response = await axios.get(`${CORS_PROXY}${source.url}`);
      const htmlData = response.data;
      
      // 创建一个临时的DOM元素来解析HTML
      const parser = new DOMParser();
      const doc = parser.parseFromString(htmlData, 'text/html');
      
      // 尝试找到新闻标题
      // 这里使用一些常见的选择器，可能需要根据具体网站调整
      const headings = Array.from(doc.querySelectorAll('h1, h2, h3, .title, .headline, article header'));
      
      const items = headings.map(heading => {
        // 尝试找到链接
        let link = '#';
        const linkElement = heading.querySelector('a') || heading.closest('a');
        if (linkElement && linkElement.href) {
          // 处理相对URL
          if (linkElement.href.startsWith('/')) {
            const url = new URL(source.url);
            link = `${url.protocol}//${url.host}${linkElement.href}`;
          } else {
            link = linkElement.href;
          }
        }
        
        return {
          title: heading.textContent.trim(),
          link,
          date: new Date(),
          description: ''
        };
      }).filter(item => item.title); // 过滤掉空标题
      
      setNewsItems(items);
    } catch (error) {
      console.error('加载HTML失败:', error);
      throw new Error('加载HTML失败');
    }
  };
  
  // 打开添加源模态框
  const openAddSourceModal = () => {
    setEditingSource(null);
    setFormData({
      name: '',
      url: '',
      type: 'rss'
    });
    setShowModal(true);
  };
  
  // 打开编辑源模态框
  const openEditSourceModal = (source) => {
    setEditingSource(source);
    setFormData({
      name: source.name,
      url: source.url,
      type: source.type
    });
    setShowModal(true);
  };
  
  // 关闭模态框
  const closeModal = () => {
    setShowModal(false);
    setEditingSource(null);
  };
  
  // 打开源列表模态框
  const openSourcesModal = () => {
    setShowSourcesModal(true);
  };
  
  // 关闭源列表模态框
  const closeSourcesModal = () => {
    setShowSourcesModal(false);
  };
  
  // 处理表单输入变化
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };
  
  // 保存源
  const saveSource = () => {
    if (!formData.name.trim() || !formData.url.trim()) {
      alert('请填写完整信息');
      return;
    }
    
    const newSource = {
      id: editingSource ? editingSource.id : Date.now(),
      name: formData.name.trim(),
      url: formData.url.trim(),
      type: formData.type
    };
    
    let updatedSources;
    
    if (editingSource) {
      // 编辑现有源
      updatedSources = sources.map(source => 
        source.id === editingSource.id ? newSource : source
      );
    } else {
      // 添加新源
      updatedSources = [...sources, newSource];
    }
    
    setSources(updatedSources);
    saveToStorage('homepage-news-sources', updatedSources);
    
    // 如果是第一个源，自动设为活动源
    if (sources.length === 0) {
      setActiveSource(newSource);
    }
    
    closeModal();
  };
  
  // 删除源
  const deleteSource = (id) => {
    if (window.confirm('确定要删除这个新闻源吗？')) {
      const updatedSources = sources.filter(source => source.id !== id);
      setSources(updatedSources);
      saveToStorage('homepage-news-sources', updatedSources);
      
      // 如果删除的是当前活动源，切换到第一个源
      if (activeSource && activeSource.id === id) {
        if (updatedSources.length > 0) {
          setActiveSource(updatedSources[0]);
        } else {
          setActiveSource(null);
          setNewsItems([]);
        }
      }
      
      // 如果是在源列表模态框中删除，不关闭模态框
      if (!showSourcesModal) {
        closeModal();
      }
    }
  };
  
  // 返回主页
  const goBack = () => {
    window.history.back();
  };
  
  return (
    <NewsContainer>
      <NewsHeader>
        <div style={{ display: 'flex', alignItems: 'center' }}>
          <BackButton onClick={goBack} title="返回">
            <ArrowBackIcon />
          </BackButton>
          <HeaderTitle>新闻</HeaderTitle>
        </div>
      </NewsHeader>
      
      <SourcesContainer>
        {sources.map(source => (
          <SourceButton 
            key={source.id} 
            active={activeSource && activeSource.id === source.id}
            onClick={() => setActiveSource(source)}
          >
            {source.type === 'rss' ? <RssFeedIcon /> : <LinkIcon />}
            {source.name}
          </SourceButton>
        ))}
        
        <AddSourceButton onClick={openSourcesModal}>
          <AddIcon />
          管理源
        </AddSourceButton>
      </SourcesContainer>
      
      <NewsContent>
        {loading ? (
          <LoadingState>加载中...</LoadingState>
        ) : error ? (
          <ErrorState>{error}</ErrorState>
        ) : newsItems.length > 0 ? (
          <NewsList>
            {newsItems.map((item, index) => (
              <NewsItem key={index} href={item.link} target="_blank" rel="noopener noreferrer">
                <NewsTitle>{item.title}</NewsTitle>
                {item.date && (
                  <NewsDate>
                    {item.date.toLocaleDateString('zh-CN', {
                      year: 'numeric',
                      month: 'long',
                      day: 'numeric'
                    })}
                  </NewsDate>
                )}
              </NewsItem>
            ))}
          </NewsList>
        ) : activeSource ? (
          <EmptyState>
            <p>没有找到新闻</p>
          </EmptyState>
        ) : (
          <EmptyState>
            <p>请添加新闻源</p>
          </EmptyState>
        )}
      </NewsContent>
      
      {/* 添加/编辑源模态框 */}
      {showModal && (
        <Modal>
          <ModalContent>
            <ModalHeader>
              <ModalTitle>{editingSource ? '编辑新闻源' : '添加新闻源'}</ModalTitle>
              <CloseButton onClick={closeModal}>&times;</CloseButton>
            </ModalHeader>
            
            <FormGroup>
              <Label>名称</Label>
              <Input 
                type="text" 
                name="name" 
                value={formData.name} 
                onChange={handleInputChange} 
                placeholder="新闻源名称"
              />
            </FormGroup>
            
            <FormGroup>
              <Label>类型</Label>
              <Select 
                name="type" 
                value={formData.type} 
                onChange={handleInputChange}
              >
                <option value="rss">RSS 订阅</option>
                <option value="html">网页</option>
              </Select>
            </FormGroup>
            
            <FormGroup>
              <Label>URL</Label>
              <Input 
                type="text" 
                name="url" 
                value={formData.url} 
                onChange={handleInputChange} 
                placeholder={formData.type === 'rss' ? 'RSS 订阅地址' : '网页地址'}
              />
            </FormGroup>
            
            <ButtonGroup>
              {editingSource && (
                <Button 
                  className="danger" 
                  onClick={() => deleteSource(editingSource.id)}
                >
                  删除
                </Button>
              )}
              <Button className="secondary" onClick={closeModal}>取消</Button>
              <Button className="primary" onClick={saveSource}>保存</Button>
            </ButtonGroup>
          </ModalContent>
        </Modal>
      )}
      
      {/* 源列表模态框 */}
      {showSourcesModal && (
        <Modal>
          <ModalContent>
            <ModalHeader>
              <ModalTitle>管理新闻源</ModalTitle>
              <CloseButton onClick={closeSourcesModal}>&times;</CloseButton>
            </ModalHeader>
            
            <Button className="primary" onClick={openAddSourceModal} style={{ width: '100%' }}>
              <AddIcon /> 添加新闻源
            </Button>
            
            <SourcesList>
              {sources.map(source => (
                <SourceItem key={source.id} type={source.type}>
                  <SourceInfo>
                    {source.type === 'rss' ? <RssFeedIcon /> : <LinkIcon />}
                    <div>
                      <SourceName>{source.name}</SourceName>
                      <SourceUrl>{source.url}</SourceUrl>
                    </div>
                  </SourceInfo>
                  <SourceActions>
                    <ActionIconButton onClick={() => openEditSourceModal(source)}>
                      <EditIcon fontSize="small" />
                    </ActionIconButton>
                    <ActionIconButton 
                      className="delete" 
                      onClick={() => deleteSource(source.id)}
                    >
                      <DeleteIcon fontSize="small" />
                    </ActionIconButton>
                  </SourceActions>
                </SourceItem>
              ))}
              
              {sources.length === 0 && (
                <EmptyState>
                  <p>暂无新闻源</p>
                </EmptyState>
              )}
            </SourcesList>
            
            <ButtonGroup>
              <Button className="secondary" onClick={closeSourcesModal}>关闭</Button>
            </ButtonGroup>
          </ModalContent>
        </Modal>
      )}
    </NewsContainer>
  );
};

export default News;
