const Slider2 = styled.span`
  position: absolute;
  cursor: pointer;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background-color: var(--border-color);
  transition: .4s;
  border-radius: 20px;
  
  &:before {
    position: absolute;
    content: "";
    height: 14px;
    width: 14px;
    left: 3px;
    bottom: 3px;
    background-color: var(--card-background);
    transition: .4s;
    border-radius: 50%;
  }
  
  input:checked + & {
    background-color: var(--primary-color);
  }
  
  input:checked + &:before {
    transform: translateX(20px);
  }
`;

const Search = () => {
  const { settings, updateSpecificSettings } = useContext(SettingsContext);
  const [query, setQuery] = useState('');
  const [autoClean, setAutoClean] = useState(settings?.search?.autoCleanUrl || false);
  const [removeNonUrl, setRemoveNonUrl] = useState(settings?.search?.removeNonUrl || false);
  const [engine, setEngine] = useState(settings?.search?.defaultEngine || 'bing');
  const [showEngineDropdown, setShowEngineDropdown] = useState(false);
  const [customEngineName, setCustomEngineName] = useState('');
  const [customEngineUrl, setCustomEngineUrl] = useState('');
  const [customEngines, setCustomEngines] = useState(settings?.search?.customEngines || []);
  const [editingEngine, setEditingEngine] = useState(null);
  const [editName, setEditName] = useState('');
  const [editUrl, setEditUrl] = useState('');
  const [testQuery, setTestQuery] = useState('测试查询[移除]');
  
  const dropdownRef = useRef(null);
  const searchFormRef = useRef(null);
  
  // 点击外部关闭下拉菜单
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target) &&
          searchFormRef.current && !searchFormRef.current.contains(event.target)) {
        setShowEngineDropdown(false);
      }
    };
    
    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);
  
  // 初始化自定义引擎
  useEffect(() => {
    if (settings?.search?.customEngines) {
      setCustomEngines(settings.search.customEngines);
    }
  }, [settings]);
  
  // 处理搜索提交
  const handleSubmit = (e) => {
    e.preventDefault();
    
    if (!query.trim()) return;
    
    try {
      let processedQuery = query;
      let originalIsUrl = false;
      
      // 首先检查原始查询是否是有效URL
      if (isValidUrl(query) || query.startsWith('file:/')) {
        originalIsUrl = true;
        
        // 如果是URL，直接跳转
        if (!autoClean) {
          if (settings?.search?.openInNewTab) {
            window.open(query, '_blank');
          } else {
            window.location.href = query;
          }
          return;
        }
      }
      
      // 如果启用了URL清理
      if (autoClean) {
        // 按照要求处理URL:
        // 1. 首先去除[]内的内容及[]本身
        // 2. 然后去除中文
        // 3. 将中文标点符号转化为英文标点
        // 4. 最后去除非URL的字符
        processedQuery = cleanSearchQuery(query);
        
        // 如果清理后的查询是有效URL，直接跳转
        if (isValidUrl(processedQuery) || processedQuery.startsWith('file:/')) {
          if (settings?.search?.openInNewTab) {
            window.open(processedQuery, '_blank');
          } else {
            window.location.href = processedQuery;
          }
          return;
        }
        
        // 如果原始查询是URL但清理后不是，使用原始URL
        if (originalIsUrl) {
          if (settings?.search?.openInNewTab) {
            window.open(query, '_blank');
          } else {
            window.location.href = query;
          }
          return;
        }
      }
      
      // 如果不是URL，使用搜索引擎
      // 检查是否是自定义引擎
      const customEngine = customEngines.find(e => e.id === engine);
      
      let searchUrl;
      if (customEngine) {
        searchUrl = customEngine.url.replace('{query}', encodeURIComponent(processedQuery));
      } else {
        searchUrl = buildSearchUrl(processedQuery, engine, removeNonUrl, customEngines);
      }
      
      if (settings?.search?.openInNewTab) {
        window.open(searchUrl, '_blank');
      } else {
        window.location.href = searchUrl;
      }
    } catch (error) {
      console.error('搜索处理错误:', error);
      // 使用默认搜索引擎作为后备
      const backupUrl = `https://www.bing.com/search?q=${encodeURIComponent(query)}`;
      window.open(backupUrl, '_blank');
    }
  };
  
  // 处理输入变化
  const handleInputChange = (e) => {
    setQuery(e.target.value);
  };
  
  // 切换自动清理URL
  const toggleAutoClean = () => {
    const newValue = !autoClean;
    setAutoClean(newValue);
    updateSpecificSettings('search.autoCleanUrl', newValue);
  };
  
  // 切换移除非URL字符
  const toggleRemoveNonUrl = () => {
    const newValue = !removeNonUrl;
    setRemoveNonUrl(newValue);
    updateSpecificSettings('search.removeNonUrl', newValue);
  };
  
  // 切换搜索引擎
  const changeEngine = (newEngine) => {
    setEngine(newEngine);
    updateSpecificSettings('search.defaultEngine', newEngine);
    setShowEngineDropdown(false);
  };
  
  // 添加自定义搜索引擎
  const addCustomEngine = () => {
    if (!customEngineName.trim() || !customEngineUrl.trim() || !customEngineUrl.includes('{query}')) {
      alert('请输入有效的名称和URL，URL中必须包含{query}作为搜索词的占位符');
      return;
    }
    
    const newEngine = {
      id: `custom_${Date.now()}`,
      name: customEngineName,
      url: customEngineUrl
    };
    
    const updatedEngines = [...customEngines, newEngine];
    setCustomEngines(updatedEngines);
    updateSpecificSettings('search.customEngines', updatedEngines);
    
    // 切换到新添加的引擎
    setEngine(newEngine.id);
    updateSpecificSettings('search.defaultEngine', newEngine.id);
    
    // 清空输入框
    setCustomEngineName('');
    setCustomEngineUrl('');
  };
  
  // 开始编辑自定义搜索引擎
  const startEditEngine = (engine) => {
    setEditingEngine(engine.id);
    setEditName(engine.name);
    setEditUrl(engine.url);
  };
  
  // 保存编辑的自定义搜索引擎
  const saveEditEngine = () => {
    if (!editName.trim() || !editUrl.trim() || !editUrl.includes('{query}')) {
      alert('请输入有效的名称和URL，URL中必须包含{query}作为搜索词的占位符');
      return;
    }
    
    const updatedEngines = customEngines.map(engine => 
      engine.id === editingEngine 
        ? { ...engine, name: editName, url: editUrl } 
        : engine
    );
    
    setCustomEngines(updatedEngines);
    updateSpecificSettings('search.customEngines', updatedEngines);
    
    // 取消编辑状态
    cancelEditEngine();
  };
  
  // 取消编辑
  const cancelEditEngine = () => {
    setEditingEngine(null);
    setEditName('');
    setEditUrl('');
  };
  
  // 删除自定义搜索引擎
  const deleteEngine = (engineId) => {
    if (window.confirm('确定要删除这个搜索引擎吗？')) {
      const updatedEngines = customEngines.filter(engine => engine.id !== engineId);
      setCustomEngines(updatedEngines);
      updateSpecificSettings('search.customEngines', updatedEngines);
      
      // 如果删除的是当前选中的引擎，切换到默认引擎
      if (engine === engineId) {
        setEngine('bing');
        updateSpecificSettings('search.defaultEngine', 'bing');
      }
    }
  };
  
  // 处理拖拽结束
  const handleDragEnd = (result) => {
    if (!result.destination) return;
    
    const sourceIndex = result.source.index;
    const destinationIndex = result.destination.index;
    
    if (sourceIndex === destinationIndex) return;
    
    const updatedEngines = [...customEngines];
    const [removed] = updatedEngines.splice(sourceIndex, 1);
    updatedEngines.splice(destinationIndex, 0, removed);
    
    setCustomEngines(updatedEngines);
    updateSpecificSettings('search.customEngines', updatedEngines);
  };
  
  // 测试自定义搜索引擎
  const testEngine = (engineUrl) => {
    try {
      // 处理测试查询
      let processedQuery = testQuery;
      if (autoClean) {
        processedQuery = cleanSearchQuery(testQuery);
      }
      
      const testUrl = engineUrl.replace('{query}', encodeURIComponent(processedQuery));
      window.open(testUrl, '_blank');
    } catch (error) {
      console.error('测试搜索引擎失败:', error);
      alert('测试失败，请检查URL格式');
    }
  };
  
  // 获取当前引擎名称
  const getCurrentEngineName = () => {
    const predefinedEngines = {
      'bing': '必应',
      'google': '谷歌',
      'baidu': '百度',
      'sogou': '搜狗',
      'mita': '秘塔AI'
    };
    
    if (predefinedEngines[engine]) {
      return predefinedEngines[engine];
    }
    
    const customEngine = customEngines.find(e => e.id === engine);
    return customEngine ? customEngine.name : '必应';
  };
  
  return (
    <SearchContainer>
      <SearchForm onSubmit={handleSubmit} ref={searchFormRef}>
        <EngineButton 
          type="button" 
          onClick={() => setShowEngineDropdown(!showEngineDropdown)}
          title="选择搜索引擎"
        >
          {getCurrentEngineName()}
          <ArrowDropDownIcon fontSize="small" />
        </EngineButton>
        <SearchInput
          type="text"
          placeholder="搜索或输入网址"
          value={query}
          onChange={handleInputChange}
          autoFocus
        />
        <CleanButton
          type="button"
          onClick={toggleAutoClean}
          active={autoClean}
          title={autoClean ? '已启用URL清理' : '已禁用URL清理'}
        >
          <CleaningServicesIcon fontSize="small" />
        </CleanButton>
        <SearchButton type="submit" title="搜索">
          <SearchIcon />
        </SearchButton>
      </SearchForm>
      
      {showEngineDropdown && (
        <EngineDropdown show={showEngineDropdown} ref={dropdownRef}>
          <EngineGrid>
            <EngineOption
              onClick={() => changeEngine('bing')}
              active={engine === 'bing'}
            >
              必应
            </EngineOption>
            <EngineOption
              onClick={() => changeEngine('google')}
              active={engine === 'google'}
            >
              谷歌
            </EngineOption>
            <EngineOption
              onClick={() => changeEngine('baidu')}
              active={engine === 'baidu'}
            >
              百度
            </EngineOption>
            <EngineOption
              onClick={() => changeEngine('sogou')}
              active={engine === 'sogou'}
            >
              搜狗
            </EngineOption>
            <EngineOption
              onClick={() => changeEngine('mita')}
              active={engine === 'mita'}
            >
              秘塔AI
            </EngineOption>
            
            {customEngines.map(customEngine => (
              <EngineOption
                key={customEngine.id}
                onClick={() => changeEngine(customEngine.id)}
                active={engine === customEngine.id}
              >
                {customEngine.name}
              </EngineOption>
            ))}
          </EngineGrid>
          
          <CustomEngineForm>
            <FormRow>
              <Label>名称:</Label>
              <Input 
                type="text" 
                value={customEngineName}
                onChange={(e) => setCustomEngineName(e.target.value)}
                placeholder="自定义引擎名称"
              />
            </FormRow>
            <FormRow>
              <Label>URL:</Label>
              <Input 
                type="text" 
                value={customEngineUrl}
                onChange={(e) => setCustomEngineUrl(e.target.value)}
                placeholder="https://example.com/search?q={query}"
              />
            </FormRow>
            <FormRow>
              <SwitchContainer>
                <SwitchLabel>URL清理</SwitchLabel>
                <Switch>
                  <input 
                    type="checkbox" 
                    checked={autoClean} 
                    onChange={toggleAutoClean}
                  />
                  <Slider2 />
                </Switch>
              </SwitchContainer>
              <AddButton onClick={addCustomEngine}>添加自定义引擎</AddButton>
            </FormRow>
          </CustomEngineForm>
          
          {customEngines.length > 0 && (
            <CustomEngineList>
              <DragDropContext onDragEnd={handleDragEnd}>
                <Droppable droppableId="custom-engines">
                  {(provided) => (
                    <div
                      {...provided.droppableProps}
                      ref={provided.innerRef}
                    >
                      {customEngines.map((customEngine, index) => (
                        editingEngine === customEngine.id ? (
                          <EditForm key={customEngine.id}>
                            <FormRow>
                              <Label>名称:</Label>
                              <Input 
                                type="text" 
                                value={editName}
                                onChange={(e) => setEditName(e.target.value)}
                              />
                            </FormRow>
                            <FormRow>
                              <Label>URL:</Label>
                              <Input 
                                type="text" 
                                value={editUrl}
                                onChange={(e) => setEditUrl(e.target.value)}
                              />
                            </FormRow>
                            <EditFormButtons>
                              <EditFormButton onClick={cancelEditEngine}>
                                <CloseIcon fontSize="small" /> 取消
                              </EditFormButton>
                              <EditFormButton primary onClick={saveEditEngine}>
                                <CheckIcon fontSize="small" /> 保存
                              </EditFormButton>
                            </EditFormButtons>
                          </EditForm>
                        ) : (
                          <Draggable 
                            key={customEngine.id} 
                            draggableId={customEngine.id} 
                            index={index}
                          >
                            {(provided, snapshot) => (
                              <div
                                ref={provided.innerRef}
                                {...provided.draggableProps}
                                style={provided.draggableProps.style}
                              >
                                <CustomEngineItem
                                  isDragging={snapshot.isDragging}
                                >
                                  <DragHandle {...provided.dragHandleProps}>
                                    <DragIndicatorIcon />
                                  </DragHandle>
                                  <EngineName active={engine === customEngine.id}>
                                    {customEngine.name}
                                  </EngineName>
                                  <EngineUrl>{customEngine.url}</EngineUrl>
                                  <ActionButtons>
                                    <TestButton 
                                      onClick={() => testEngine(customEngine.url)}
                                      title="测试搜索引擎"
                                    >
                                      测试
                                    </TestButton>
                                    <ActionButton 
                                      onClick={() => startEditEngine(customEngine)}
                                      title="编辑"
                                    >
                                      <EditIcon fontSize="small" />
                                    </ActionButton>
                                    <ActionButton 
                                      onClick={() => deleteEngine(customEngine.id)}
                                      title="删除"
                                    >
                                      <DeleteIcon fontSize="small" />
                                    </ActionButton>
                                  </ActionButtons>
                                </CustomEngineItem>
                              </div>
                            )}
                          </Draggable>
                        )
                      ))}
                      {provided.placeholder}
                    </div>
                  )}
                </Droppable>
              </DragDropContext>
            </CustomEngineList>
          )}
        </EngineDropdown>
      )}
    </SearchContainer>
  );
};

export default Search;
