import React, { useState, useEffect, useCallback } from 'react';
import styled from 'styled-components';
import { SettingsProvider } from './contexts/SettingsContext';
import { ThemeProvider } from './contexts/ThemeContext';
import { getFromStorage, saveToStorage } from './utils/storage';

// 组件导入
import Header from './components/Header/Header';
import Search from './components/Search/Search';
import ModuleSwitch from './components/ModuleSwitch/ModuleSwitch';
import SettingsPanel from './settings/SettingsPanel';
import Loading from './components/Loading/Loading';

// 模块导入
import Calendar from './modules/Calendar/Calendar';
import Bookmark from './modules/Bookmark/Bookmark';
import Todo from './modules/Todo/Todo';
import Note from './modules/Note/Note';
import Quote from './modules/Quote/Quote';
import News from './modules/News/News';

// 模式导入
import ZenMode from './modes/ZenMode/ZenMode';
import CurtainMode from './modes/CurtainMode/CurtainMode';

const AppContainer = styled.div`
  min-height: 100vh;
  background: var(--background-color, #ffffff);
  color: var(--text-color, #333333);
  font-family: var(--font-family, 'Microsoft YaHei', sans-serif);
  transition: all 0.3s ease;
  position: relative;
  overflow-x: hidden;
`;

const MainContent = styled.main`
  padding: 20px;
  max-width: 1200px;
  margin: 0 auto;
  min-height: calc(100vh - 80px);
  display: flex;
  flex-direction: column;
  gap: 20px;
`;


const ErrorContainer = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  min-height: 100vh;
  background: var(--background-color, #ffffff);
  color: var(--text-color, #333333);
  text-align: center;
  padding: 20px;
`;

const ErrorContent = styled.div`
  max-width: 500px;
  
  h2 {
    color: #e74c3c;
    margin-bottom: 15px;
  }
  
  p {
    margin-bottom: 20px;
    line-height: 1.6;
  }
  
  button {
    background: var(--primary-color, #4a90e2);
    color: white;
    border: none;
    padding: 10px 20px;
    border-radius: 5px;
    cursor: pointer;
    font-size: 16px;
    
    &:hover {
      opacity: 0.9;
    }
  }
`;

// 模块映射
const moduleComponents = {
  calendar: Calendar,
  bookmark: Bookmark,
  todo: Todo,
  note: Note,
  quote: Quote,
  news: News
};

function App() {
  const [currentModule, setCurrentModule] = useState('quote');
  const [isSettingsOpen, setIsSettingsOpen] = useState(false);
  const [currentMode, setCurrentMode] = useState('normal');
  const [isInitialized, setIsInitialized] = useState(false);
  const [error, setError] = useState(null);
  const [isLoading, setIsLoading] = useState(true);

  // 错误边界处理
  const handleError = useCallback((error, errorInfo) => {
    console.error('应用错误:', error, errorInfo);
    setError(error);
  }, []);

  // 应用完整设置
  const applyFullSettings = useCallback((settings) => {
    try {
      const root = document.documentElement;
      const body = document.body;
      
      // 设置页面标题
      document.title = '時';
      
      // 应用基础CSS变量
      root.style.setProperty('--primary-color', settings.linkColor || '#4a90e2');
      root.style.setProperty('--transition-speed', '0.3s');
      
      // 应用主题
      const theme = settings.theme || 'light';
      if (theme === 'dark') {
        root.style.setProperty('--background-color', '#121212');
        root.style.setProperty('--card-background', '#1e1e1e');
        root.style.setProperty('--border-color', '#333333');
        root.style.setProperty('--text-color', settings.textColor || '#e0e0e0');
        root.style.setProperty('--text-secondary-color', '#a0a0a0');
        root.style.setProperty('--primary-color-light', 'rgba(74, 144, 226, 0.2)');
        body.classList.add('dark-mode');
      } else {
        root.style.setProperty('--background-color', '#ffffff');
        root.style.setProperty('--card-background', '#f8f8f8');
        root.style.setProperty('--border-color', '#e0e0e0');
        root.style.setProperty('--text-color', settings.textColor || '#333333');
        root.style.setProperty('--text-secondary-color', '#666666');
        root.style.setProperty('--primary-color-light', 'rgba(74, 144, 226, 0.1)');
        body.classList.remove('dark-mode');
      }
      
      // 应用字体设置
      if (settings.fontFamily) {
        root.style.setProperty('--font-family', settings.fontFamily);
      }
      
      // 应用背景设置
      body.style.background = '';
      body.style.backgroundImage = '';
      body.style.backgroundSize = '';
      body.style.backgroundPosition = '';
      body.style.backgroundRepeat = '';
      body.style.backgroundAttachment = '';
      body.style.opacity = '';
      body.style.filter = '';
      
      if (settings.showBackground !== false) {
        if (settings.backgroundType === 'color' && settings.backgroundColor) {
          body.style.background = settings.backgroundColor;
        } else if (settings.backgroundType === 'image' && settings.backgroundImage) {
          body.style.backgroundImage = `url("${settings.backgroundImage}")`;
          body.style.backgroundSize = settings.backgroundFit || 'cover';
          body.style.backgroundPosition = 'center';
          body.style.backgroundRepeat = 'no-repeat';
          body.style.backgroundAttachment = 'fixed';
        } else if (settings.backgroundType === 'gradient' && settings.gradientColors) {
          const { gradientType = 'linear', gradientDirection = 'to right', gradientColors, gradientStops = [0, 100] } = settings;
          if (gradientColors && gradientColors.length >= 2) {
            const gradient = `${gradientType}-gradient(${gradientDirection}, ${gradientColors[0]} ${gradientStops[0]}%, ${gradientColors[1]} ${gradientStops[1]}%)`;
            body.style.background = gradient;
          }
        }
        
        // 应用背景效果
        if (settings.backgroundOpacity !== undefined && settings.backgroundOpacity !== 1) {
          body.style.opacity = settings.backgroundOpacity;
        }
        
        const filters = [];
        if (settings.backgroundBrightness !== undefined && settings.backgroundBrightness !== 1) {
          filters.push(`brightness(${settings.backgroundBrightness})`);
        }
        if (settings.backgroundBlur !== undefined && settings.backgroundBlur > 0) {
          filters.push(`blur(${settings.backgroundBlur}px)`);
        }
        if (filters.length > 0) {
          body.style.filter = filters.join(' ');
        }
      } else {
        body.style.background = theme === 'dark' ? '#121212' : '#ffffff';
      }
    } catch (error) {
      console.error('应用设置时出错:', error);
    }
  }, []);

  // 初始化应用
  useEffect(() => {
    const initializeApp = async () => {
      try {
        setIsLoading(true);
        
        // 设置页面标题
        document.title = '時';
        
        // 延迟初始化以避免闪烁
        await new Promise(resolve => setTimeout(resolve, 100));
        
        // 获取保存的设置
        const savedSettings = getFromStorage('homepage-settings', {});
        
        // 确定启动模块
        let startupModule = 'quote'; // 默认为一语
        
        if (savedSettings.startupModule) {
          if (savedSettings.startupModule.type === 'last') {
            // 继承上次使用的模块
            const lastModule = getFromStorage('last-active-module', 'quote');
            startupModule = lastModule;
          } else if (savedSettings.startupModule.type === 'specific') {
            // 使用指定的模块
            startupModule = savedSettings.startupModule.module || 'quote';
          }
        } else {
          // 如果没有设置，检查是否有上次使用的模块
          const lastModule = getFromStorage('last-active-module', null);
          if (lastModule) {
            startupModule = lastModule;
          }
        }
        
        // 验证模块是否存在
        if (!moduleComponents[startupModule]) {
          startupModule = 'quote';
        }
        
        setCurrentModule(startupModule);
        
        // 应用完整设置
        applyFullSettings(savedSettings);
        
        setIsInitialized(true);
      } catch (error) {
        console.error('初始化应用时出错:', error);
        handleError(error);
      } finally {
        setIsLoading(false);
      }
    };

    initializeApp();
  }, [handleError, applyFullSettings]);

  // 处理模块切换
  const handleModuleChange = useCallback((module) => {
    try {
      if (moduleComponents[module]) {
        setCurrentModule(module);
        // 保存当前模块为上次使用的模块
        saveToStorage('last-active-module', module);
      }
    } catch (error) {
      console.error('切换模块时出错:', error);
      handleError(error);
    }
  }, [handleError]);

  // 处理模式切换
  const handleModeChange = useCallback((mode) => {
    try {
      setCurrentMode(mode);
    } catch (error) {
      console.error('切换模式时出错:', error);
      handleError(error);
    }
  }, [handleError]);

  // 渲染当前模块
  const renderCurrentModule = useCallback(() => {
    try {
      const ModuleComponent = moduleComponents[currentModule];
      if (!ModuleComponent) {
        return <div>模块不存在: {currentModule}</div>;
      }
      return <ModuleComponent />;
    } catch (error) {
      console.error('渲染模块时出错:', error);
      return <div>模块加载失败，请刷新页面重试</div>;
    }
  }, [currentModule]);

  // 错误状态
  if (error) {
    return (
      <ErrorContainer>
        <ErrorContent>
          <h2>应用出现错误</h2>
          <p>抱歉，应用遇到了一个错误。请尝试刷新页面。</p>
          <p style={{ fontSize: '14px', color: 'var(--text-secondary-color)' }}>
            错误信息: {error.message}
          </p>
          <button onClick={() => window.location.reload()}>
            刷新页面
          </button>
        </ErrorContent>
      </ErrorContainer>
    );
  }

  // 加载状态
  if (isLoading || !isInitialized) {
    return <Loading text="正在加载..." subtext="请稍候，正在初始化应用程序" />;
  }

  // 特殊模式渲染
  if (currentMode === 'zen') {
    return (
      <ThemeProvider>
        <SettingsProvider>
          <ZenMode onModeChange={handleModeChange} />
        </SettingsProvider>
      </ThemeProvider>
    );
  }

  if (currentMode === 'curtain') {
    return (
      <ThemeProvider>
        <SettingsProvider>
          <CurtainMode onModeChange={handleModeChange} />
        </SettingsProvider>
      </ThemeProvider>
    );
  }

  // 正常模式渲染
  return (
    <ThemeProvider>
      <SettingsProvider>
        <AppContainer>
          <Header 
            onSettingsClick={() => setIsSettingsOpen(true)}
            onModeChange={handleModeChange}
            currentMode={currentMode}
          />
          
          <MainContent>
            <Search />
            
            <ModuleSwitch 
              activeModule={currentModule}
              onModuleChange={handleModuleChange}
            />
            
            {renderCurrentModule()}
          </MainContent>
          
        <SettingsPanel 
          isOpen={isSettingsOpen}
          onClose={() => setIsSettingsOpen(false)}
          onModeChange={handleModeChange}
        />
        </AppContainer>
      </SettingsProvider>
    </ThemeProvider>
  );
}

export default App;
