// 代理服务器URL，用于解决跨域问题
const CORS_PROXY = 'https://corsproxy.io/?';

const News = () => {
  // 状态
  const [sources, setSources] = useState([]);
  const [activeSource, setActiveSource] = useState(null);
  const [newsItems, setNewsItems] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [showSourcesModal, setShowSourcesModal] = useState(false);
  const [editingSource, setEditingSource] = useState(null);
  
  const [formData, setFormData] = useState({
    name: '',
    url: '',
    type: 'rss' // rss 或 html
  });
  
  // 初始化数据
  useEffect(() => {
    const savedSources = getFromStorage('homepage-news-sources', []);
    setSources(savedSources);
    
    if (savedSources.length > 0) {
      setActiveSource(savedSources[0]);
    }
  }, []);
  
  // 当活动源变化时，加载新闻
  useEffect(() => {
    if (activeSource) {
      loadNews(activeSource);
    }
  }, [activeSource]);
  
  // 加载新闻
  const loadNews = async (source) => {
    setLoading(true);
    setError(null);
    setNewsItems([]);
    
    try {
      if (source.type === 'rss') {
        await loadRssNews(source);
      } else {
        await loadHtmlNews(source);
      }
    } catch (err) {
      console.error('加载新闻失败:', err);
      setError(`加载失败: ${err.message}`);
    } finally {
      setLoading(false);
    }
  };
  
  // 加载RSS新闻
  const loadRssNews = async (source) => {
    try {
      // 使用代理服务器解决跨域问题
      const response = await axios.get(`${CORS_PROXY}${source.url}`, {
        headers: {
          'Accept': 'application/rss+xml, application/xml, text/xml, */*'
        }
      });
      const xmlData = response.data;
      
      // 解析XML
      xml2js.parseString(xmlData, (err, result) => {
        if (err) {
          console.error('XML解析错误:', err);
          throw new Error('解析RSS失败');
        }
        
        let items = [];
        
        try {
          // 处理不同的RSS格式
          if (result.rss && result.rss.channel && result.rss.channel[0].item) {
            // 标准RSS 2.0
            items = result.rss.channel[0].item.map(item => ({
              title: item.title ? item.title[0] : '无标题',
              link: item.link ? item.link[0] : '#',
              date: item.pubDate ? new Date(item.pubDate[0]) : new Date(),
              description: item.description ? item.description[0] : ''
            }));
          } else if (result.feed && result.feed.entry) {
            // Atom格式
            items = result.feed.entry.map(entry => ({
              title: entry.title ? (typeof entry.title[0] === 'string' ? entry.title[0] : entry.title[0]._) : '无标题',
              link: entry.link ? (entry.link[0].$ ? entry.link[0].$.href : (typeof entry.link[0] === 'string' ? entry.link[0] : '#')) : '#',
              date: entry.updated ? new Date(entry.updated[0]) : new Date(),
              description: entry.summary ? (typeof entry.summary[0] === 'string' ? entry.summary[0] : entry.summary[0]._) : ''
            }));
          } else if (result['rdf:RDF'] && result['rdf:RDF'].item) {
            // RDF格式
            items = result['rdf:RDF'].item.map(item => ({
              title: item.title ? item.title[0] : '无标题',
              link: item.link ? item.link[0] : '#',
              date: item['dc:date'] ? new Date(item['dc:date'][0]) : new Date(),
              description: item.description ? item.description[0] : ''
            }));
          } else {
            // 尝试遍历结果找到可能的项目
            const findItems = (obj) => {
              if (!obj || typeof obj !== 'object') return null;
              
              // 直接查找item或entry数组
              if (obj.item && Array.isArray(obj.item)) {
                return obj.item.map(item => ({
                  title: item.title ? (typeof item.title === 'string' ? item.title : item.title[0]) : '无标题',
                  link: item.link ? (typeof item.link === 'string' ? item.link : item.link[0]) : '#',
                  date: item.pubDate ? new Date(typeof item.pubDate === 'string' ? item.pubDate : item.pubDate[0]) : new Date(),
                  description: item.description ? (typeof item.description === 'string' ? item.description : item.description[0]) : ''
                }));
              }
              
              if (obj.entry && Array.isArray(obj.entry)) {
                return obj.entry.map(entry => ({
                  title: entry.title ? (typeof entry.title === 'string' ? entry.title : entry.title[0]) : '无标题',
                  link: entry.link ? (typeof entry.link === 'string' ? entry.link : entry.link[0]) : '#',
                  date: entry.updated ? new Date(typeof entry.updated === 'string' ? entry.updated : entry.updated[0]) : new Date(),
                  description: entry.summary ? (typeof entry.summary === 'string' ? entry.summary : entry.summary[0]) : ''
                }));
              }
              
              // 递归查找
              for (const key in obj) {
                if (typeof obj[key] === 'object' && obj[key] !== null) {
                  const found = findItems(obj[key]);
                  if (found) return found;
                }
              }
              
              return null;
            };
            
            const foundItems = findItems(result);
            if (foundItems) {
              items = foundItems;
            } else {
              console.error('未找到支持的RSS格式:', result);
              throw new Error('不支持的RSS格式');
            }
          }
        } catch (parseError) {
          console.error('RSS内容解析错误:', parseError, result);
          throw new Error('RSS内容解析失败');
        }
        
        setNewsItems(items);
      });
    } catch (error) {
      console.error('加载RSS失败:', error);
      throw new Error(`加载RSS失败: ${error.message}`);
    }
  };
  
  // 加载HTML新闻
  const loadHtmlNews = async (source) => {
    try {
      // 使用代理服务器解决跨域问题
      const response = await axios.get(`${CORS_PROXY}${source.url}`);
      const htmlData = response.data;
      
      // 创建一个临时的DOM元素来解析HTML
      const parser = new DOMParser();
      const doc = parser.parseFromString(htmlData, 'text/html');
      
      // 尝试找到新闻标题
      // 这里使用一些常见的选择器，可能需要根据具体网站调整
      const headings = Array.from(doc.querySelectorAll('h1, h2, h3, .title, .headline, article header'));
      
      const items = headings.map(heading => {
        // 尝试找到链接
        let link = '#';
        const linkElement = heading.querySelector('a') || heading.closest('a');
        if (linkElement && linkElement.href) {
          // 处理相对URL
          if (linkElement.href.startsWith('/')) {
            const url = new URL(source.url);
            link = `${url.protocol}//${url.host}${linkElement.href}`;
          } else {
            link = linkElement.href;
          }
        }
        
        return {
          title: heading.textContent.trim(),
          link,
          date: new Date(),
          description: ''
        };
      }).filter(item => item.title); // 过滤掉空标题
      
      setNewsItems(items);
    } catch (error) {
      console.error('加载HTML失败:', error);
      throw new Error('加载HTML失败');
    }
  };
  
  // 打开添加源模态框
  const openAddSourceModal = () => {
    setEditingSource(null);
    setFormData({
      name: '',
      url: '',
      type: 'rss'
    });
    setShowModal(true);
  };
  
  // 打开编辑源模态框
  const openEditSourceModal = (source) => {
    setEditingSource(source);
    setFormData({
      name: source.name,
      url: source.url,
      type: source.type
    });
    setShowModal(true);
  };
  
  // 关闭模态框
  const closeModal = () => {
    setShowModal(false);
    setEditingSource(null);
  };
  
  // 打开源列表模态框
  const openSourcesModal = () => {
    setShowSourcesModal(true);
  };
  
  // 关闭源列表模态框
  const closeSourcesModal = () => {
    setShowSourcesModal(false);
  };
  
  // 处理表单输入变化
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };
  
  // 保存源
  const saveSource = () => {
    if (!formData.name.trim() || !formData.url.trim()) {
      alert('请填写完整信息');
      return;
    }
    
    const newSource = {
      id: editingSource ? editingSource.id : Date.now(),
      name: formData.name.trim(),
      url: formData.url.trim(),
      type: formData.type
    };
    
    let updatedSources;
    
    if (editingSource) {
      // 编辑现有源
      updatedSources = sources.map(source => 
        source.id === editingSource.id ? newSource : source
      );
    } else {
      // 添加新源
      updatedSources = [...sources, newSource];
    }
    
    setSources(updatedSources);
    saveToStorage('homepage-news-sources', updatedSources);
    
    // 如果是第一个源，自动设为活动源
    if (sources.length === 0) {
      setActiveSource(newSource);
    }
    
    closeModal();
  };
  
  // 删除源
  const deleteSource = (id) => {
    if (window.confirm('确定要删除这个新闻源吗？')) {
      const updatedSources = sources.filter(source => source.id !== id);
      setSources(updatedSources);
      saveToStorage('homepage-news-sources', updatedSources);
      
      // 如果删除的是当前活动源，切换到第一个源
      if (activeSource && activeSource.id === id) {
        if (updatedSources.length > 0) {
          setActiveSource(updatedSources[0]);
        } else {
          setActiveSource(null);
          setNewsItems([]);
        }
      }
      
      // 如果是在源列表模态框中删除，不关闭模态框
      if (!showSourcesModal) {
        closeModal();
      }
    }
  };
  
  // 返回主页
  const goBack = () => {
    window.history.back();
  };
  
  return (
    <NewsContainer>
      <NewsHeader>
        <div style={{ display: 'flex', alignItems: 'center' }}>
          <BackButton onClick={goBack} title="返回">
            <ArrowBackIcon />
          </BackButton>
          <HeaderTitle>新闻</HeaderTitle>
        </div>
      </NewsHeader>
      
      <SourcesContainer>
        {sources.map(source => (
          <SourceButton 
            key={source.id} 
            active={activeSource && activeSource.id === source.id}
            onClick={() => setActiveSource(source)}
          >
            {source.type === 'rss' ? <RssFeedIcon /> : <LinkIcon />}
            {source.name}
          </SourceButton>
        ))}
        
        <AddSourceButton onClick={openSourcesModal}>
          <AddIcon />
          管理源
        </AddSourceButton>
      </SourcesContainer>
      
      <NewsContent>
        {loading ? (
          <LoadingState>加载中...</LoadingState>
        ) : error ? (
          <ErrorState>{error}</ErrorState>
        ) : newsItems.length > 0 ? (
          <NewsList>
            {newsItems.map((item, index) => (
              <NewsItem key={index} href={item.link} target="_blank" rel="noopener noreferrer">
                <NewsTitle>{item.title}</NewsTitle>
                {item.date && (
                  <NewsDate>
                    {item.date.toLocaleDateString('zh-CN', {
                      year: 'numeric',
                      month: 'long',
                      day: 'numeric'
                    })}
                  </NewsDate>
                )}
              </NewsItem>
            ))}
          </NewsList>
        ) : activeSource ? (
          <EmptyState>
            <p>没有找到新闻</p>
          </EmptyState>
        ) : (
          <EmptyState>
            <p>请添加新闻源</p>
          </EmptyState>
        )}
      </NewsContent>
      
      {/* 添加/编辑源模态框 */}
      {showModal && (
        <Modal>
          <ModalContent>
            <ModalHeader>
              <ModalTitle>{editingSource ? '编辑新闻源' : '添加新闻源'}</ModalTitle>
              <CloseButton onClick={closeModal}>&times;</CloseButton>
            </ModalHeader>
            
            <FormGroup>
              <Label>名称</Label>
              <Input 
                type="text" 
                name="name" 
                value={formData.name} 
                onChange={handleInputChange} 
                placeholder="新闻源名称"
              />
            </FormGroup>
            
            <FormGroup>
              <Label>类型</Label>
              <Select 
                name="type" 
                value={formData.type} 
                onChange={handleInputChange}
              >
                <option value="rss">RSS 订阅</option>
                <option value="html">网页</option>
              </Select>
            </FormGroup>
            
            <FormGroup>
              <Label>URL</Label>
              <Input 
                type="text" 
                name="url" 
                value={formData.url} 
                onChange={handleInputChange} 
                placeholder={formData.type === 'rss' ? 'RSS 订阅地址' : '网页地址'}
              />
            </FormGroup>
            
            <ButtonGroup>
              {editingSource && (
                <Button 
                  className="danger" 
                  onClick={() => deleteSource(editingSource.id)}
                >
                  删除
                </Button>
              )}
              <Button className="secondary" onClick={closeModal}>取消</Button>
              <Button className="primary" onClick={saveSource}>保存</Button>
            </ButtonGroup>
          </ModalContent>
        </Modal>
      )}
      
      {/* 源列表模态框 */}
      {showSourcesModal && (
        <Modal>
          <ModalContent>
            <ModalHeader>
              <ModalTitle>管理新闻源</ModalTitle>
              <CloseButton onClick={closeSourcesModal}>&times;</CloseButton>
            </ModalHeader>
            
            <Button className="primary" onClick={openAddSourceModal} style={{ width: '100%' }}>
              <AddIcon /> 添加新闻源
            </Button>
            
            <SourcesList>
              {sources.map(source => (
                <SourceItem key={source.id} type={source.type}>
                  <SourceInfo>
                    {source.type === 'rss' ? <RssFeedIcon /> : <LinkIcon />}
                    <div>
                      <SourceName>{source.name}</SourceName>
                      <SourceUrl>{source.url}</SourceUrl>
                    </div>
                  </SourceInfo>
                  <SourceActions>
                    <ActionIconButton onClick={() => openEditSourceModal(source)}>
                      <EditIcon fontSize="small" />
                    </ActionIconButton>
                    <ActionIconButton 
                      className="delete" 
                      onClick={() => deleteSource(source.id)}
                    >
                      <DeleteIcon fontSize="small" />
                    </ActionIconButton>
                  </SourceActions>
                </SourceItem>
              ))}
              
              {sources.length === 0 && (
                <EmptyState>
                  <p>暂无新闻源</p>
                </EmptyState>
              )}
            </SourcesList>
            
            <ButtonGroup>
              <Button className="secondary" onClick={closeSourcesModal}>关闭</Button>
            </ButtonGroup>
          </ModalContent>
        </Modal>
      )}
    </NewsContainer>
  );
};

export default News;
