import React from 'react';
import styled from 'styled-components';

// 图标导入
import CalendarMonthIcon from '@mui/icons-material/CalendarMonth';
import BookmarkIcon from '@mui/icons-material/Bookmark';
import ChecklistIcon from '@mui/icons-material/Checklist';
import NoteIcon from '@mui/icons-material/Note';
import FormatQuoteIcon from '@mui/icons-material/FormatQuote';
import NewspaperIcon from '@mui/icons-material/Newspaper';

const ModuleSwitchContainer = styled.div`
  display: flex;
  justify-content: center;
  margin: 20px 0;
  flex-wrap: wrap;
  gap: 10px;
`;

const ModuleButton = styled.button`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  padding: 10px;
  border: 1px solid var(--border-color);
  background-color: ${props => props.active ? 'var(--primary-color)' : 'var(--card-background)'};
  color: ${props => props.active ? 'white' : 'var(--icon-color)'};
  border-radius: 8px;
  cursor: pointer;
  transition: all var(--transition-speed);
  width: 80px;
  height: 80px;
  box-shadow: 0 2px 5px var(--shadow-color);
  
  &:hover {
    transform: translateY(-3px);
    box-shadow: 0 5px 15px var(--shadow-color);
    background-color: ${props => props.active ? 'var(--primary-color)' : 'var(--hover-background)'};
  }
  
  svg {
    font-size: 24px;
    margin-bottom: 5px;
  }
`;

const ModuleLabel = styled.span`
  font-size: 12px;
  color: ${props => props.active ? 'white' : 'var(--text-color)'};
`;

const ModuleSwitch = ({ activeModule, onModuleChange }) => {
  // 模块配置
  const modules = [
    {
      id: 'calendar',
      label: '日历',
      icon: <CalendarMonthIcon />
    },
    {
      id: 'bookmark',
      label: '书签',
      icon: <BookmarkIcon />
    },
    {
      id: 'todo',
      label: '待办',
      icon: <ChecklistIcon />
    },
    {
      id: 'note',
      label: '笔记',
      icon: <NoteIcon />
    },
    {
      id: 'quote',
      label: '一语',
      icon: <FormatQuoteIcon />
    },
    {
      id: 'news',
      label: '新闻',
      icon: <NewspaperIcon />
    }
  ];
  
  return (
    <ModuleSwitchContainer>
      {modules.map(module => (
        <ModuleButton
          key={module.id}
          active={activeModule === module.id}
          onClick={() => onModuleChange(module.id)}
          title={module.label}
        >
          {module.icon}
          <ModuleLabel active={activeModule === module.id}>{module.label}</ModuleLabel>
        </ModuleButton>
      ))}
    </ModuleSwitchContainer>
  );
};

export default ModuleSwitch;
