import React, { createContext, useState, useEffect, useContext } from 'react';
import { getFromStorage, saveToStorage } from '../utils/storage';

// 创建上下文
export const SettingsContext = createContext();

// 默认设置
const defaultSettings = {
  theme: 'light',
  background: {
    type: 'color',
    value: '#ffffff',
    opacity: 1,
    brightness: 1,
    blurAmount: 0,
    size: 'cover',
    repeat: 'no-repeat',
    overlayOpacity: 0
  },
    search: {
      engine: 'bing',
      removeNonUrl: false,
      autoCleanUrl: false,
      defaultEngine: 'bing',
      openInNewTab: false,
      customEngines: []
    },
  modules: {
    calendar: {
      enabled: true,
      showZhuJiLi: false
    },
    bookmark: {
      enabled: true
    },
    todo: {
      enabled: true,
      notificationSound: true
    },
    note: {
      enabled: true
    },
    quote: {
      enabled: true,
      fontSize: 16,
      quotes: ['既然胜负未分,则应力挽狂澜']
    },
    news: {
      enabled: true,
      sources: []
    }
  },
  zenMode: {
    clockSize: 200,
    focusTime: 25
  },
  curtainMode: {
    fontSize: 24,
    speed: 2,
    direction: 'up'
  },
  startupModule: {
    type: 'last', // 'last' 或 'specific'
    module: 'quote' // 默认为"一语"模块
  }
};

// 设置提供者组件
export const SettingsProvider = ({ children }) => {
  // 状态
  const [settings, setSettings] = useState(null);
  const [isLoaded, setIsLoaded] = useState(false);
  
  // 初始化设置
  useEffect(() => {
    const savedSettings = getFromStorage('homepage-settings', defaultSettings);
    setSettings(savedSettings);
    setIsLoaded(true);
  }, []);
  
  // 更新设置
  const updateSettings = (newSettings) => {
    setSettings(newSettings);
    saveToStorage('homepage-settings', newSettings);
  };
  
  // 更新特定设置
  const updateSpecificSettings = (path, value) => {
    const pathArray = path.split('.');
    const newSettings = { ...settings };
    
    let current = newSettings;
    for (let i = 0; i < pathArray.length - 1; i++) {
      current = current[pathArray[i]];
    }
    
    current[pathArray[pathArray.length - 1]] = value;
    
    setSettings(newSettings);
    saveToStorage('homepage-settings', newSettings);
  };
  
  // 导出设置
  const exportSettings = () => {
    const dataStr = JSON.stringify(settings, null, 2);
    const dataUri = 'data:application/json;charset=utf-8,'+ encodeURIComponent(dataStr);
    
    const exportFileDefaultName = 'homepage-settings.json';
    
    const linkElement = document.createElement('a');
    linkElement.setAttribute('href', dataUri);
    linkElement.setAttribute('download', exportFileDefaultName);
    linkElement.click();
  };
  
  // 导入设置
  const importSettings = (jsonString) => {
    try {
      const newSettings = JSON.parse(jsonString);
      setSettings(newSettings);
      saveToStorage('homepage-settings', newSettings);
      return true;
    } catch (error) {
      console.error('导入设置失败:', error);
      return false;
    }
  };
  
  // 重置设置
  const resetSettings = () => {
    setSettings(defaultSettings);
    saveToStorage('homepage-settings', defaultSettings);
  };
  
  return (
    <SettingsContext.Provider 
      value={{ 
        settings, 
        isLoaded, 
        updateSettings, 
        updateSpecificSettings,
        exportSettings,
        importSettings,
        resetSettings
      }}
    >
      {children}
    </SettingsContext.Provider>
  );
};

// 自定义钩子，用于访问设置上下文
export const useSettings = () => {
  const context = useContext(SettingsContext);
  if (!context) {
    throw new Error('useSettings必须在SettingsProvider内部使用');
  }
  return context;
};
