import React, { useState, useEffect, useContext } from 'react';
import styled from 'styled-components';

// 组件导入
import Header from './components/Header/Header';
import Search from './components/Search/Search';
import ModuleSwitch from './components/ModuleSwitch/ModuleSwitch';
import SettingsPanel from './settings/SettingsPanel';

// 模块导入
import Calendar from './modules/Calendar/Calendar';
import Bookmark from './modules/Bookmark/Bookmark';
import Todo from './modules/Todo/Todo';
import Note from './modules/Note/Note';
import Quote from './modules/Quote/Quote';
import News from './modules/News/News';

// 模式导入
import ZenMode from './modes/ZenMode/ZenMode';
import CurtainMode from './modes/CurtainMode/CurtainMode';

// 上下文导入
import { ThemeProvider } from './contexts/ThemeContext';
import { SettingsProvider, SettingsContext } from './contexts/SettingsContext';

// 工具函数导入
import { getFromStorage, saveToStorage } from './utils/storage';

const AppContainer = styled.div`
  min-height: 100vh;
  display: flex;
  flex-direction: column;
  background-color: var(--background-color);
  color: var(--text-color);
  transition: background-color var(--transition-speed), color var(--transition-speed);
  position: relative;
  overflow: hidden;
`;

const BackgroundLayer = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  z-index: 0;
  background-image: ${props => props.backgroundImage ? `url(${props.backgroundImage})` : 'none'};
  background-color: ${props => props.backgroundColor || 'transparent'};
  background-size: ${props => props.backgroundSize || 'cover'};
  background-position: center;
  background-repeat: ${props => props.backgroundRepeat || 'no-repeat'};
  opacity: ${props => props.opacity || 1};
  filter: brightness(${props => props.brightness || 1});
`;

const BlurLayer = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  z-index: 1;
  backdrop-filter: blur(${props => props.blurAmount || 0}px);
  background-color: rgba(0, 0, 0, ${props => props.overlayOpacity || 0});
`;

const ContentLayer = styled.div`
  position: relative;
  z-index: 2;
  flex: 1;
  display: flex;
  flex-direction: column;
  padding: 20px;
`;

const MainContent = styled.main`
  flex: 1;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: flex-start;
  padding: 20px 0;
`;

const ModuleContainer = styled.div`
  width: 100%;
  max-width: 1200px;
  margin: 0 auto;
`;

const BottomControls = styled.div`
  position: fixed;
  bottom: 20px;
  right: 20px;
  display: flex;
  gap: 10px;
  z-index: 100;
`;

const ModeButton = styled.button`
  width: 40px;
  height: 40px;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  background-color: var(--primary-color);
  color: white;
  border: 1px solid var(--border-color);
  cursor: pointer;
  box-shadow: 0 2px 10px var(--shadow-color);
  
  &:hover {
    transform: translateY(-3px);
    box-shadow: 0 5px 15px var(--shadow-color);
  }
  
  svg {
    font-size: 20px;
  }
`;

const App = () => {
  return (
    <ThemeProvider>
      <SettingsProvider>
        <AppContent />
      </SettingsProvider>
    </ThemeProvider>
  );
};

// 将主要内容移到单独的组件中，这样可以使用 SettingsContext
const AppContent = () => {
  // 状态
  const [activeModule, setActiveModule] = useState('quote');
  const [showSettings, setShowSettings] = useState(false);
  const [currentMode, setCurrentMode] = useState('normal'); // normal, zen, curtain
  
  // 从 SettingsContext 获取设置
  const { settings, isLoaded, updateSettings } = useContext(SettingsContext);
  
  // 初始化主题
  useEffect(() => {
    if (settings) {
      document.documentElement.setAttribute('data-theme', settings.theme);
      // 添加额外的深色模式适配
      if (settings.theme === 'dark') {
        document.body.classList.add('dark-mode');
      } else {
        document.body.classList.remove('dark-mode');
      }
    }
  }, [settings]);
  
  // 处理模块切换
  const handleModuleChange = (moduleId) => {
    setActiveModule(moduleId);
    saveToStorage('homepage-active-module', moduleId);
  };
  
  // 渲染当前模块
  const renderActiveModule = () => {
    switch (activeModule) {
      case 'calendar':
        return <Calendar />;
      case 'bookmark':
        return <Bookmark />;
      case 'todo':
        return <Todo />;
      case 'note':
        return <Note />;
      case 'quote':
        return <Quote />;
      case 'news':
        return <News />;
      default:
        return <Calendar />;
    }
  };
  
  // 如果设置尚未加载，显示加载中
  if (!isLoaded || !settings) {
    return <div>加载中...</div>;
  }
  
  // 渲染当前模式
  const renderCurrentMode = () => {
    switch (currentMode) {
      case 'zen':
        return <ZenMode onBack={() => setCurrentMode('normal')} />;
      case 'curtain':
        return <CurtainMode onBack={() => setCurrentMode('normal')} />;
      case 'normal':
      default:
        return (
          <AppContainer>
            {/* 背景层 */}
            <BackgroundLayer 
              backgroundImage={settings.background.type === 'image' ? settings.background.value : null}
              backgroundColor={settings.background.type === 'color' ? settings.background.value : null}
              backgroundSize={settings.background.size || 'cover'}
              backgroundRepeat={settings.background.repeat || 'no-repeat'}
              opacity={settings.background.opacity || 1}
              brightness={settings.background.brightness || 1}
            />
            
            {/* 模糊层 */}
            <BlurLayer 
              blurAmount={settings.background.blurAmount || 0}
              overlayOpacity={settings.background.overlayOpacity || 0}
            />
            
            {/* 内容层 */}
            <ContentLayer>
              <Header />
              <Search />
              <ModuleSwitch 
                activeModule={activeModule}
                onModuleChange={handleModuleChange}
              />
              
              <MainContent>
                <ModuleContainer>
                  {renderActiveModule()}
                </ModuleContainer>
              </MainContent>
            </ContentLayer>
            
            {/* 底部控制按钮 */}
            <BottomControls>
              <ModeButton 
                onClick={() => setCurrentMode('zen')}
                title="禅模式"
              >
                禅
              </ModeButton>
              <ModeButton 
                onClick={() => setCurrentMode('curtain')}
                title="幕模式"
              >
                幕
              </ModeButton>
              <ModeButton 
                onClick={() => setShowSettings(!showSettings)}
                title="设置"
              >
                设
              </ModeButton>
            </BottomControls>
            
            {/* 设置面板 */}
            <SettingsPanel 
              isOpen={showSettings}
              onClose={() => setShowSettings(false)}
            />
          </AppContainer>
        );
    }
  };

  return renderCurrentMode();
};

export default App;
