import React, { useState, useRef, useEffect } from 'react';
import styled from 'styled-components';
import { getFaviconUrl } from '../../../utils/urlHandler';
import EmojiEmotionsIcon from '@mui/icons-material/EmojiEmotions';
import ImageIcon from '@mui/icons-material/Image';
import LinkIcon from '@mui/icons-material/Link';

const Modal = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background-color: rgba(0, 0, 0, 0.5);
  display: flex;
  align-items: center;
  justify-content: center;
  z-index: 1000;
`;

const ModalContent = styled.div`
  background-color: var(--background-color);
  padding: 20px;
  border-radius: 8px;
  width: 90%;
  max-width: 400px;
  box-shadow: 0 5px 15px rgba(0, 0, 0, 0.3);
`;

const ModalHeader = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 20px;
`;

const ModalTitle = styled.h3`
  margin: 0;
  font-size: 18px;
  font-weight: 500;
`;

const CloseButton = styled.button`
  background: none;
  border: none;
  cursor: pointer;
  font-size: 20px;
  color: var(--text-color);
`;

const FormGroup = styled.div`
  margin-bottom: 15px;
`;

const Label = styled.label`
  display: block;
  margin-bottom: 5px;
  font-size: 14px;
`;

const Input = styled.input`
  width: 100%;
  padding: 8px;
  border: 1px solid var(--border-color);
  border-radius: 4px;
  background-color: var(--background-color);
  color: var(--text-color);
`;

const Select = styled.select`
  width: 100%;
  padding: 8px;
  border: 1px solid var(--border-color);
  border-radius: 4px;
  background-color: var(--background-color);
  color: var(--text-color);
`;

const ButtonGroup = styled.div`
  display: flex;
  justify-content: flex-end;
  gap: 10px;
  margin-top: 20px;
`;

const Button = styled.button`
  padding: 8px 16px;
  border: none;
  border-radius: 4px;
  cursor: pointer;
  font-size: 14px;
  
  &.primary {
    background-color: var(--primary-color);
    color: white;
    
    &:hover {
      background-color: #3a7bc8;
    }
  }
  
  &.secondary {
    background-color: var(--card-background);
    color: var(--text-color);
    border: 1px solid var(--border-color);
    
    &:hover {
      background-color: rgba(0, 0, 0, 0.05);
    }
  }
`;

const IconOptions = styled.div`
  display: flex;
  gap: 10px;
  margin-bottom: 15px;
`;

const IconOption = styled.button`
  background-color: ${props => props.active ? 'var(--primary-color)' : 'var(--card-background)'};
  color: ${props => props.active ? 'white' : 'var(--text-color)'};
  border: 1px solid ${props => props.active ? 'var(--primary-color)' : 'var(--border-color)'};
  border-radius: 4px;
  padding: 8px;
  display: flex;
  align-items: center;
  justify-content: center;
  cursor: pointer;
  flex: 1;
  
  &:hover {
    background-color: ${props => props.active ? 'var(--primary-color)' : 'rgba(0, 0, 0, 0.05)'};
  }
`;

const EmojiPicker = styled.div`
  display: grid;
  grid-template-columns: repeat(8, 1fr);
  gap: 5px;
  max-height: 200px;
  overflow-y: auto;
  margin-top: 10px;
  padding: 5px;
  border: 1px solid var(--border-color);
  border-radius: 4px;
`;

const EmojiButton = styled.button`
  background: none;
  border: none;
  font-size: 20px;
  cursor: pointer;
  padding: 5px;
  border-radius: 4px;
  
  &:hover {
    background-color: rgba(0, 0, 0, 0.05);
  }
`;

const FileInput = styled.input`
  display: none;
`;

const FileInputLabel = styled.label`
  display: block;
  padding: 8px;
  background-color: var(--card-background);
  color: var(--text-color);
  border: 1px solid var(--border-color);
  border-radius: 4px;
  cursor: pointer;
  text-align: center;
  margin-top: 10px;
  
  &:hover {
    background-color: rgba(0, 0, 0, 0.05);
  }
`;

const ImagePreview = styled.div`
  width: 100%;
  height: 100px;
  margin-top: 10px;
  border: 1px solid var(--border-color);
  border-radius: 4px;
  display: flex;
  align-items: center;
  justify-content: center;
  overflow: hidden;
  
  img {
    max-width: 100%;
    max-height: 100%;
    object-fit: contain;
  }
`;

// Â∏∏Áî®Ë°®ÊÉÖÁ¨¶Âè∑ÂàóË°®
const commonEmojis = [
  'üòÄ', 'üòÉ', 'üòÑ', 'üòÅ', 'üòÜ', 'üòÖ', 'üòÇ', 'ü§£', 'üòä', 'üòá',
  'üôÇ', 'üôÉ', 'üòâ', 'üòå', 'üòç', 'ü•∞', 'üòò', 'üòó', 'üòô', 'üòö',
  'üëç', 'üëé', 'üëè', 'üôå', 'üëê', 'ü§≤', 'ü§ù', 'üôè', '‚úåÔ∏è', 'ü§û',
  'üß†', 'üëÅÔ∏è', 'üëÄ', 'üëÇ', 'üëÉ', 'üëÑ', 'üë∂', 'üë¶', 'üëß', 'üë®',
  'üê∂', 'üê±', 'üê≠', 'üêπ', 'üê∞', 'ü¶ä', 'üêª', 'üêº', 'üê®', 'üêØ',
  'üçè', 'üçé', 'üçê', 'üçä', 'üçã', 'üçå', 'üçâ', 'üçá', 'üçì', 'üçà',
  '‚öΩ', 'üèÄ', 'üèà', '‚öæ', 'ü•é', 'üéæ', 'üèê', 'üèâ', 'üé±', 'üèì',
  'üåç', 'üåé', 'üåè', 'üåï', 'üåñ', 'üåó', 'üåò', 'üåë', 'üåí', 'üåì',
  'üè†', 'üè°', 'üè¢', 'üè£', 'üè§', 'üè•', 'üè¶', 'üè®', 'üè©', 'üè™',
  'üöó', 'üöï', 'üöô', 'üöå', 'üöé', 'üèéÔ∏è', 'üöì', 'üöë', 'üöí', 'üöê',
  '‚åö', 'üì±', 'üíª', '‚å®Ô∏è', 'üñ•Ô∏è', 'üñ®Ô∏è', 'üñ±Ô∏è', 'üñ≤Ô∏è', 'üì∑', 'üì∏',
  'üìö', 'üìñ', 'üîñ', 'üìë', 'üóÇÔ∏è', 'üìÇ', 'üìÅ', 'üì∞', 'üóûÔ∏è', 'üìì',
  'üéÆ', 'üïπÔ∏è', 'üé≤', '‚ôüÔ∏è', 'üé≠', 'üé®', 'üé¨', 'üé§', 'üéß', 'üéº',
  'üèÜ', 'ü•á', 'ü•à', 'ü•â', 'üèÖ', 'üéñÔ∏è', 'üèµÔ∏è', 'üéóÔ∏è', 'üé´', 'üéüÔ∏è',
  'üõí', 'üõçÔ∏è', 'üß≥', 'üåÇ', '‚òÇÔ∏è', 'üßµ', 'üß∂', 'üëì', 'üï∂Ô∏è', 'üëî'
];

const BookmarkModal = ({ 
  isOpen, 
  onClose, 
  onSave, 
  bookmark = null, 
  folders = [] 
}) => {
  const [formData, setFormData] = useState({
    name: '',
    url: '',
    icon: '',
    folderId: ''
  });
  
  const [iconType, setIconType] = useState('favicon'); // favicon, emoji, image
  const [selectedEmoji, setSelectedEmoji] = useState('üîñ');
  const [imagePreview, setImagePreview] = useState('');
  const fileInputRef = useRef(null);
  
  // ÂàùÂßãÂåñË°®ÂçïÊï∞ÊçÆ
  useEffect(() => {
    if (bookmark) {
      setFormData({
        name: bookmark.name,
        url: bookmark.url,
        icon: bookmark.icon,
        folderId: bookmark.folderId || ''
      });
      
      // Á°ÆÂÆöÂõæÊ†áÁ±ªÂûã
      if (bookmark.icon.startsWith('data:image') || bookmark.icon.startsWith('http') || bookmark.icon.startsWith('file:')) {
        setIconType('image');
        setImagePreview(bookmark.icon);
      } else if (bookmark.icon.length <= 2) {
        setIconType('emoji');
        setSelectedEmoji(bookmark.icon);
      } else {
        setIconType('favicon');
      }
    } else {
      setFormData({
        name: '',
        url: '',
        icon: '',
        folderId: ''
      });
      setIconType('favicon');
      setSelectedEmoji('üîñ');
      setImagePreview('');
    }
  }, [bookmark, isOpen]);
  
  // Â§ÑÁêÜË°®ÂçïËæìÂÖ•ÂèòÂåñ
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };
  
  // Â§ÑÁêÜURLÂèòÂåñÔºåËá™Âä®Ëé∑ÂèñÁΩëÁ´ôÂõæÊ†á
  const handleUrlChange = (e) => {
    const { value } = e.target;
    setFormData(prev => ({
      ...prev,
      url: value
    }));
    
    if (iconType === 'favicon' && value) {
      const faviconUrl = getFaviconUrl(value);
      setFormData(prev => ({
        ...prev,
        icon: faviconUrl
      }));
    }
  };
  
  // Â§ÑÁêÜÂõæÊ†áÁ±ªÂûãÂèòÂåñ
  const handleIconTypeChange = (type) => {
    setIconType(type);
    
    // Ê†πÊçÆÁ±ªÂûãËÆæÁΩÆÈªòËÆ§ÂõæÊ†á
    if (type === 'emoji') {
      setFormData(prev => ({
        ...prev,
        icon: selectedEmoji
      }));
    } else if (type === 'favicon') {
      if (formData.url) {
        const faviconUrl = getFaviconUrl(formData.url);
        setFormData(prev => ({
          ...prev,
          icon: faviconUrl
        }));
      } else {
        setFormData(prev => ({
          ...prev,
          icon: ''
        }));
      }
    } else if (type === 'image') {
      setFormData(prev => ({
        ...prev,
        icon: imagePreview || ''
      }));
    }
  };
  
  // Â§ÑÁêÜË°®ÊÉÖÁ¨¶Âè∑ÈÄâÊã©
  const handleEmojiSelect = (emoji) => {
    setSelectedEmoji(emoji);
    setFormData(prev => ({
      ...prev,
      icon: emoji
    }));
  };
  
  // Â§ÑÁêÜÂõæÁâáÊñá‰ª∂ÈÄâÊã©
  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (event) => {
        setImagePreview(event.target.result);
        setFormData(prev => ({
          ...prev,
          icon: event.target.result
        }));
      };
      reader.readAsDataURL(file);
    }
  };
  
  // ‰øùÂ≠ò‰π¶Á≠æ
  const handleSave = () => {
    if (!formData.name.trim() || !formData.url.trim()) {
      alert('ËØ∑Â°´ÂÜôÂÆåÊï¥‰ø°ÊÅØ');
      return;
    }
    
    onSave({
      ...formData,
      name: formData.name.trim(),
      url: formData.url.trim(),
      folderId: formData.folderId || null
    });
  };
  
  if (!isOpen) return null;
  
  return (
    <Modal>
      <ModalContent>
        <ModalHeader>
          <ModalTitle>{bookmark ? 'ÁºñËæë‰π¶Á≠æ' : 'Ê∑ªÂä†‰π¶Á≠æ'}</ModalTitle>
          <CloseButton onClick={onClose}>&times;</CloseButton>
        </ModalHeader>
        
        <FormGroup>
          <Label>ÂêçÁß∞</Label>
          <Input 
            type="text" 
            name="name" 
            value={formData.name} 
            onChange={handleInputChange} 
            placeholder="‰π¶Á≠æÂêçÁß∞"
          />
        </FormGroup>
        
        <FormGroup>
          <Label>URL</Label>
          <Input 
            type="text" 
            name="url" 
            value={formData.url} 
            onChange={handleUrlChange} 
            placeholder="https://example.com"
          />
        </FormGroup>
        
        <FormGroup>
          <Label>Êñá‰ª∂Â§π</Label>
          <Select 
            name="folderId" 
            value={formData.folderId} 
            onChange={handleInputChange}
          >
            <option value="">Ê†πÁõÆÂΩï</option>
            {folders.map(folder => (
              <option key={folder.id} value={folder.id}>{folder.name}</option>
            ))}
          </Select>
        </FormGroup>
        
        <FormGroup>
          <Label>ÂõæÊ†á</Label>
          <IconOptions>
            <IconOption 
              active={iconType === 'favicon'} 
              onClick={() => handleIconTypeChange('favicon')}
            >
              <LinkIcon />
            </IconOption>
            <IconOption 
              active={iconType === 'emoji'} 
              onClick={() => handleIconTypeChange('emoji')}
            >
              <EmojiEmotionsIcon />
            </IconOption>
            <IconOption 
              active={iconType === 'image'} 
              onClick={() => handleIconTypeChange('image')}
            >
              <ImageIcon />
            </IconOption>
          </IconOptions>
          
          {iconType === 'emoji' && (
            <EmojiPicker>
              {commonEmojis.map(emoji => (
                <EmojiButton 
                  key={emoji} 
                  onClick={() => handleEmojiSelect(emoji)}
                  style={{ 
                    backgroundColor: selectedEmoji === emoji ? 'rgba(0, 0, 0, 0.1)' : 'transparent' 
                  }}
                >
                  {emoji}
                </EmojiButton>
              ))}
            </EmojiPicker>
          )}
          
          {iconType === 'image' && (
            <>
              <FileInputLabel>
                ÈÄâÊã©ÂõæÁâá
                <FileInput 
                  type="file" 
                  accept="image/*" 
                  ref={fileInputRef}
                  onChange={handleFileChange}
                />
              </FileInputLabel>
              
              {imagePreview && (
                <ImagePreview>
                  <img src={imagePreview} alt="È¢ÑËßà" />
                </ImagePreview>
              )}
            </>
          )}
        </FormGroup>
        
        <ButtonGroup>
          <Button className="secondary" onClick={onClose}>ÂèñÊ∂à</Button>
          <Button className="primary" onClick={handleSave}>‰øùÂ≠ò</Button>
        </ButtonGroup>
      </ModalContent>
    </Modal>
  );
};

export default BookmarkModal;
