import React, { useState, useEffect, useRef } from 'react';
import styled from 'styled-components';
import { saveToStorage, getFromStorage } from '../utils/storage';
import { handleUrlChange } from '../utils/urlHandler';
import { useSettings } from '../contexts/SettingsContext';

// 图标导入
import CloseIcon from '@mui/icons-material/Close';
import WallpaperIcon from '@mui/icons-material/Wallpaper';
import TextFormatIcon from '@mui/icons-material/TextFormat';
import BackupIcon from '@mui/icons-material/Backup';
import RestoreIcon from '@mui/icons-material/Restore';
import DarkModeIcon from '@mui/icons-material/DarkMode';
import HomeIcon from '@mui/icons-material/Home';

const SettingsContainer = styled.div`
  position: fixed;
  top: 0;
  right: ${props => props.isOpen ? '0' : '-400px'};
  width: 400px;
  height: 100vh;
  background-color: var(--card-background);
  box-shadow: -5px 0 15px rgba(0, 0, 0, 0.1);
  transition: right 0.3s ease;
  z-index: 1000;
  display: flex;
  flex-direction: column;
  overflow: hidden;
`;

const SettingsHeader = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 20px;
  border-bottom: 1px solid var(--border-color);
`;

const SettingsTitle = styled.h2`
  margin: 0;
  font-size: 20px;
  font-weight: 500;
`;

const CloseButton = styled.button`
  background: none;
  border: none;
  cursor: pointer;
  color: var(--text-color);
  display: flex;
  align-items: center;
  justify-content: center;
  
  &:hover {
    color: var(--primary-color);
  }
`;

const SettingsContent = styled.div`
  flex: 1;
  overflow-y: auto;
  padding: 20px;
`;

const SettingsSection = styled.div`
  margin-bottom: 30px;
`;

const SectionTitle = styled.h3`
  margin: 0 0 15px 0;
  font-size: 16px;
  font-weight: 500;
  display: flex;
  align-items: center;
  gap: 8px;
  
  svg {
    color: var(--primary-color);
  }
`;

const FormGroup = styled.div`
  margin-bottom: 15px;
`;

const Label = styled.label`
  display: block;
  margin-bottom: 5px;
  font-size: 14px;
`;

const Input = styled.input`
  width: 100%;
  padding: 8px;
  border: 1px solid var(--border-color);
  border-radius: 4px;
  background-color: var(--background-color);
  color: var(--text-color);
`;

const Select = styled.select`
  width: 100%;
  padding: 8px;
  border: 1px solid var(--border-color);
  border-radius: 4px;
  background-color: var(--background-color);
  color: var(--text-color);
`;

const Slider = styled.input`
  width: 100%;
  margin: 10px 0;
`;


const ColorPicker = styled.input`
  width: 100%;
  height: 40px;
  border: 1px solid var(--border-color);
  border-radius: 4px;
  cursor: pointer;
`;

const Switch = styled.label`
  position: relative;
  display: inline-block;
  width: 50px;
  height: 24px;
  
  input {
    opacity: 0;
    width: 0;
    height: 0;
  }
`;

const Slider2 = styled.span`
  position: absolute;
  cursor: pointer;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background-color: #ccc;
  transition: .4s;
  border-radius: 24px;
  
  &:before {
    position: absolute;
    content: "";
    height: 16px;
    width: 16px;
    left: 4px;
    bottom: 4px;
    background-color: white;
    transition: .4s;
    border-radius: 50%;
  }
  
  input:checked + & {
    background-color: var(--primary-color);
  }
  
  input:checked + &:before {
    transform: translateX(26px);
  }
`;

const SwitchContainer = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
`;

const SwitchLabel = styled.span`
  font-size: 14px;
`;

const GradientPreview = styled.div`
  width: 100%;
  height: 40px;
  border-radius: 4px;
  margin-top: 10px;
  background: ${props => props.gradient};
`;

const ButtonGroup = styled.div`
  display: flex;
  gap: 10px;
  margin-top: 20px;
`;

const Button = styled.button`
  padding: 8px 16px;
  border: none;
  border-radius: 4px;
  cursor: pointer;
  font-size: 14px;
  display: flex;
  align-items: center;
  gap: 5px;
  
  &.primary {
    background-color: var(--primary-color);
    color: white;
    
    &:hover {
      background-color: #3a7bc8;
    }
  }
  
  &.secondary {
    background-color: var(--card-background);
    color: var(--text-color);
    border: 1px solid var(--border-color);
    
    &:hover {
      background-color: rgba(0, 0, 0, 0.05);
    }
  }
`;

const FileInput = styled.input`
  display: none;
`;

const FileInputLabel = styled.label`
  display: block;
  padding: 8px 16px;
  background-color: var(--card-background);
  color: var(--text-color);
  border: 1px solid var(--border-color);
  border-radius: 4px;
  cursor: pointer;
  text-align: center;
  font-size: 14px;
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 5px;
  
  &:hover {
    background-color: rgba(0, 0, 0, 0.05);
  }
`;

const ImagePreview = styled.div`
  width: 100%;
  height: 100px;
  margin-top: 10px;
  border: 1px solid var(--border-color);
  border-radius: 4px;
  display: flex;
  align-items: center;
  justify-content: center;
  overflow: hidden;
  background-color: var(--background-color);
  
  img {
    max-width: 100%;
    max-height: 100%;
    object-fit: contain;
  }
`;

const SettingsPanel = ({ isOpen, onClose }) => {
  // 从上下文获取设置
  // const { } = useSettings();
  
  // 状态
  const [settings, setSettings] = useState({
    theme: 'light',
    backgroundType: 'color',
    backgroundColor: '#ffffff',
    backgroundImage: '',
    backgroundOpacity: 1,
    backgroundBrightness: 1,
    backgroundBlur: 0,
    backgroundFit: 'cover',
    textColor: '#000000',
    linkColor: '#4a90e2',
    fontFamily: 'Arial, sans-serif',
    gradientType: 'linear',
    gradientDirection: 'to right',
    gradientColors: ['#4a90e2', '#9b59b6'],
    gradientStops: [0, 100],
    showBackground: true,
    startupModuleType: 'last',
    startupModule: 'quote'
  });
  
  const [imagePreview, setImagePreview] = useState('');
  const fileInputRef = useRef(null);
  const importRef = useRef(null);
  
  // 初始化数据
  useEffect(() => {
    const savedSettings = getFromStorage('homepage-settings', {
      theme: 'light',
      backgroundType: 'color',
      backgroundColor: '#ffffff',
      backgroundImage: '',
      backgroundOpacity: 1,
      backgroundBrightness: 1,
      backgroundBlur: 0,
      backgroundFit: 'cover',
      textColor: '#000000',
      linkColor: '#4a90e2',
      fontFamily: 'Arial, sans-serif',
      gradientType: 'linear',
      gradientDirection: 'to right',
      gradientColors: ['#4a90e2', '#9b59b6'],
      gradientStops: [0, 100],
      showBackground: true
    });
    
    // 获取启动模块设置
    const globalSettings = getFromStorage('homepage-settings', {});
    if (globalSettings.startupModule) {
      savedSettings.startupModuleType = globalSettings.startupModule.type || 'last';
      savedSettings.startupModule = globalSettings.startupModule.module || 'quote';
    } else {
      savedSettings.startupModuleType = 'last';
      savedSettings.startupModule = 'quote';
    }
    
    setSettings(savedSettings);
    
    if (savedSettings.backgroundImage) {
      setImagePreview(savedSettings.backgroundImage);
    }
    
    // 应用设置
    applySettings(savedSettings);
  }, []);
  
  // 处理设置变化
  const handleSettingChange = (key, value) => {
    const updatedSettings = { ...settings, [key]: value };
    setSettings(updatedSettings);
    saveToStorage('homepage-settings', updatedSettings);
    applySettings(updatedSettings);
    
    // 如果是启动模块设置，更新全局设置中的启动模块配置
    if (key === 'startupModuleType') {
      const globalSettings = getFromStorage('homepage-settings', {});
      if (!globalSettings.startupModule) {
        globalSettings.startupModule = {};
      }
      globalSettings.startupModule.type = value;
      saveToStorage('homepage-settings', globalSettings);
    }
    
    if (key === 'startupModule') {
      const globalSettings = getFromStorage('homepage-settings', {});
      if (!globalSettings.startupModule) {
        globalSettings.startupModule = {};
      }
      globalSettings.startupModule.module = value;
      saveToStorage('homepage-settings', globalSettings);
    }
  };
  
  // 处理渐变颜色变化
  const handleGradientColorChange = (index, value) => {
    const updatedColors = [...settings.gradientColors];
    updatedColors[index] = value;
    
    const updatedSettings = { ...settings, gradientColors: updatedColors };
    setSettings(updatedSettings);
    saveToStorage('homepage-settings', updatedSettings);
    applySettings(updatedSettings);
  };
  
  // 处理渐变位置变化
  const handleGradientStopChange = (index, value) => {
    const updatedStops = [...settings.gradientStops];
    updatedStops[index] = parseInt(value);
    
    const updatedSettings = { ...settings, gradientStops: updatedStops };
    setSettings(updatedSettings);
    saveToStorage('homepage-settings', updatedSettings);
    applySettings(updatedSettings);
  };
  
  // 处理图片文件选择
  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (event) => {
        const imageUrl = event.target.result;
        setImagePreview(imageUrl);
        handleSettingChange('backgroundImage', imageUrl);
      };
      reader.readAsDataURL(file);
    }
  };
  
  // 处理URL或HTML标签输入
  const handleImageUrlChange = (e) => {
    const value = e.target.value;
    
    // 处理URL
    const processedUrl = handleUrlChange(value);
    
    if (processedUrl) {
      setImagePreview(processedUrl);
      handleSettingChange('backgroundImage', processedUrl);
    } else {
      // 尝试从HTML标签中提取src
      const srcMatch = value.match(/src=["'](.*?)["']/);
      if (srcMatch && srcMatch[1]) {
        setImagePreview(srcMatch[1]);
        handleSettingChange('backgroundImage', srcMatch[1]);
      } else {
        setImagePreview(value);
        handleSettingChange('backgroundImage', value);
      }
    }
  };
  
  // 切换主题
  const toggleTheme = () => {
    const newTheme = settings.theme === 'light' ? 'dark' : 'light';
    handleSettingChange('theme', newTheme);
  };
  
  // 导出设置
  const exportSettings = () => {
    const settingsJson = JSON.stringify(settings, null, 2);
    const blob = new Blob([settingsJson], { type: 'application/json' });
    const url = URL.createObjectURL(blob);
    
    const a = document.createElement('a');
    a.href = url;
    a.download = 'homepage-settings.json';
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);
  };
  
  // 导入设置
  const importSettings = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (event) => {
        try {
          const importedSettings = JSON.parse(event.target.result);
          setSettings(importedSettings);
          saveToStorage('homepage-settings', importedSettings);
          applySettings(importedSettings);
          
          if (importedSettings.backgroundImage) {
            setImagePreview(importedSettings.backgroundImage);
          }
          
          alert('设置导入成功');
        } catch (error) {
          alert('导入失败，文件格式不正确');
        }
      };
      reader.readAsText(file);
    }
  };
  
  // 应用设置到CSS变量
  const applySettings = (settings) => {
    const root = document.documentElement;
    
    // 应用主题
    if (settings.theme === 'dark') {
      root.style.setProperty('--background-color', '#121212');
      root.style.setProperty('--card-background', '#1e1e1e');
      root.style.setProperty('--border-color', '#333333');
      root.style.setProperty('--shadow-color', 'rgba(0, 0, 0, 0.3)');
      root.style.setProperty('--text-color', '#e0e0e0');
      root.style.setProperty('--text-secondary-color', '#a0a0a0');
    } else {
      root.style.setProperty('--background-color', '#ffffff');
      root.style.setProperty('--card-background', '#f8f8f8');
      root.style.setProperty('--border-color', '#e0e0e0');
      root.style.setProperty('--shadow-color', 'rgba(0, 0, 0, 0.1)');
      root.style.setProperty('--text-color', '#333333');
      root.style.setProperty('--text-secondary-color', '#666666');
    }
    
    // 应用颜色设置
    root.style.setProperty('--text-color', settings.textColor);
    root.style.setProperty('--primary-color', settings.linkColor);
    
    // 应用字体设置
    root.style.setProperty('--font-family', settings.fontFamily);
    
    // 应用背景设置
    const body = document.body;
    
    if (settings.showBackground) {
      if (settings.backgroundType === 'color') {
        body.style.background = settings.backgroundColor;
        body.style.backgroundImage = 'none';
      } else if (settings.backgroundType === 'image' && settings.backgroundImage) {
        body.style.backgroundImage = `url(${settings.backgroundImage})`;
        body.style.backgroundSize = settings.backgroundFit;
        body.style.backgroundPosition = 'center';
        body.style.backgroundRepeat = 'no-repeat';
      } else if (settings.backgroundType === 'gradient') {
        const { gradientType, gradientDirection, gradientColors, gradientStops } = settings;
        const gradient = `${gradientType}-gradient(${gradientDirection}, ${gradientColors[0]} ${gradientStops[0]}%, ${gradientColors[1]} ${gradientStops[1]}%)`;
        body.style.background = gradient;
      }
      
      // 应用背景效果
      body.style.opacity = settings.backgroundOpacity;
      body.style.filter = `brightness(${settings.backgroundBrightness}) blur(${settings.backgroundBlur}px)`;
    } else {
      // 隐藏背景
      body.style.background = 'none';
      body.style.backgroundImage = 'none';
      body.style.opacity = 1;
      body.style.filter = 'none';
    }
  };
  
  // 生成渐变预览
  const getGradientPreview = () => {
    const { gradientType, gradientDirection, gradientColors, gradientStops } = settings;
    return `${gradientType}-gradient(${gradientDirection}, ${gradientColors[0]} ${gradientStops[0]}%, ${gradientColors[1]} ${gradientStops[1]}%)`;
  };
  
  return (
    <SettingsContainer isOpen={isOpen}>
      <SettingsHeader>
        <SettingsTitle>设置</SettingsTitle>
        <CloseButton onClick={onClose}>
          <CloseIcon />
        </CloseButton>
      </SettingsHeader>
      
      <SettingsContent>
        <SettingsSection>
          <SectionTitle>
            <DarkModeIcon />
            主题
          </SectionTitle>
          
          <SwitchContainer>
            <SwitchLabel>深色模式</SwitchLabel>
            <Switch>
              <input 
                type="checkbox" 
                checked={settings.theme === 'dark'} 
                onChange={toggleTheme}
              />
              <Slider2 />
            </Switch>
          </SwitchContainer>
        </SettingsSection>
        
        <SettingsSection>
          <SectionTitle>
            <WallpaperIcon />
            背景设置
          </SectionTitle>
          
          <SwitchContainer>
            <SwitchLabel>显示背景</SwitchLabel>
            <Switch>
              <input 
                type="checkbox" 
                checked={settings.showBackground} 
                onChange={(e) => handleSettingChange('showBackground', e.target.checked)}
              />
              <Slider2 />
            </Switch>
          </SwitchContainer>
          
          {settings.showBackground && (
            <>
              <FormGroup>
                <Label>背景类型</Label>
                <Select 
                  value={settings.backgroundType} 
                  onChange={(e) => handleSettingChange('backgroundType', e.target.value)}
                >
                  <option value="color">纯色</option>
                  <option value="image">图片</option>
                  <option value="gradient">渐变</option>
                </Select>
              </FormGroup>
              
              {settings.backgroundType === 'color' && (
                <FormGroup>
                  <Label>背景颜色</Label>
                  <ColorPicker 
                    type="color" 
                    value={settings.backgroundColor} 
                    onChange={(e) => handleSettingChange('backgroundColor', e.target.value)}
                  />
                </FormGroup>
              )}
              
              {settings.backgroundType === 'image' && (
                <>
                  <FormGroup>
                    <Label>背景图片</Label>
                    <FileInputLabel>
                      <WallpaperIcon />
                      选择本地图片
                      <FileInput 
                        type="file" 
                        accept="image/*" 
                        ref={fileInputRef}
                        onChange={handleFileChange}
                      />
                    </FileInputLabel>
                  </FormGroup>
                  
                  <FormGroup>
                    <Label>或输入图片URL/HTML标签</Label>
                    <Input 
                      type="text" 
                      value={settings.backgroundImage} 
                      onChange={handleImageUrlChange}
                      placeholder="输入URL或HTML标签的src属性"
                    />
                  </FormGroup>
                  
                  {imagePreview && (
                    <ImagePreview>
                      <img src={imagePreview} alt="背景预览" />
                    </ImagePreview>
                  )}
                  
                  <FormGroup>
                    <Label>适应方式</Label>
                    <Select 
                      value={settings.backgroundFit} 
                      onChange={(e) => handleSettingChange('backgroundFit', e.target.value)}
                    >
                      <option value="cover">填充</option>
                      <option value="contain">适应</option>
                      <option value="100% 100%">拉伸</option>
                      <option value="repeat">平铺</option>
                      <option value="auto">居中</option>
                    </Select>
                  </FormGroup>
                </>
              )}
              
              {settings.backgroundType === 'gradient' && (
                <>
                  <FormGroup>
                    <Label>渐变类型</Label>
                    <Select 
                      value={settings.gradientType} 
                      onChange={(e) => handleSettingChange('gradientType', e.target.value)}
                    >
                      <option value="linear">线性渐变</option>
                      <option value="radial">径向渐变</option>
                    </Select>
                  </FormGroup>
                  
                  {settings.gradientType === 'linear' && (
                    <FormGroup>
                      <Label>渐变方向</Label>
                      <Select 
                        value={settings.gradientDirection} 
                        onChange={(e) => handleSettingChange('gradientDirection', e.target.value)}
                      >
                        <option value="to right">从左到右</option>
                        <option value="to left">从右到左</option>
                        <option value="to bottom">从上到下</option>
                        <option value="to top">从下到上</option>
                        <option value="to bottom right">左上到右下</option>
                        <option value="to bottom left">右上到左下</option>
                        <option value="to top right">左下到右上</option>
                        <option value="to top left">右下到左上</option>
                      </Select>
                    </FormGroup>
                  )}
                  
                  <FormGroup>
                    <Label>颜色1</Label>
                    <ColorPicker 
                      type="color" 
                      value={settings.gradientColors[0]} 
                      onChange={(e) => handleGradientColorChange(0, e.target.value)}
                    />
                  </FormGroup>
                  
                  <FormGroup>
                    <Label>位置1 ({settings.gradientStops[0]}%)</Label>
                    <Slider 
                      type="range" 
                      min="0" 
                      max="100" 
                      value={settings.gradientStops[0]} 
                      onChange={(e) => handleGradientStopChange(0, e.target.value)}
                    />
                  </FormGroup>
                  
                  <FormGroup>
                    <Label>颜色2</Label>
                    <ColorPicker 
                      type="color" 
                      value={settings.gradientColors[1]} 
                      onChange={(e) => handleGradientColorChange(1, e.target.value)}
                    />
                  </FormGroup>
                  
                  <FormGroup>
                    <Label>位置2 ({settings.gradientStops[1]}%)</Label>
                    <Slider 
                      type="range" 
                      min="0" 
                      max="100" 
                      value={settings.gradientStops[1]} 
                      onChange={(e) => handleGradientStopChange(1, e.target.value)}
                    />
                  </FormGroup>
                  
                  <GradientPreview gradient={getGradientPreview()} />
                </>
              )}
              
              <FormGroup>
                <Label>透明度 ({Math.round(settings.backgroundOpacity * 100)}%)</Label>
                <Slider 
                  type="range" 
                  min="0" 
                  max="1" 
                  step="0.01" 
                  value={settings.backgroundOpacity} 
                  onChange={(e) => handleSettingChange('backgroundOpacity', parseFloat(e.target.value))}
                />
              </FormGroup>
              
              <FormGroup>
                <Label>亮度 ({Math.round(settings.backgroundBrightness * 100)}%)</Label>
                <Slider 
                  type="range" 
                  min="0.1" 
                  max="2" 
                  step="0.1" 
                  value={settings.backgroundBrightness} 
                  onChange={(e) => handleSettingChange('backgroundBrightness', parseFloat(e.target.value))}
                />
              </FormGroup>
              
              <FormGroup>
                <Label>模糊 ({settings.backgroundBlur}px)</Label>
                <Slider 
                  type="range" 
                  min="0" 
                  max="20" 
                  value={settings.backgroundBlur} 
                  onChange={(e) => handleSettingChange('backgroundBlur', parseInt(e.target.value))}
                />
              </FormGroup>
            </>
          )}
        </SettingsSection>
        
        <SettingsSection>
          <SectionTitle>
            <TextFormatIcon />
            文字设置
          </SectionTitle>
          
          <FormGroup>
            <Label>文字颜色</Label>
            <ColorPicker 
              type="color" 
              value={settings.textColor} 
              onChange={(e) => handleSettingChange('textColor', e.target.value)}
            />
          </FormGroup>
          
          <FormGroup>
            <Label>链接颜色</Label>
            <ColorPicker 
              type="color" 
              value={settings.linkColor} 
              onChange={(e) => handleSettingChange('linkColor', e.target.value)}
            />
          </FormGroup>
          
          <FormGroup>
            <Label>字体</Label>
            <Select 
              value={settings.fontFamily} 
              onChange={(e) => handleSettingChange('fontFamily', e.target.value)}
            >
              <option value="Arial, sans-serif">Arial</option>
              <option value="'Microsoft YaHei', sans-serif">微软雅黑</option>
              <option value="'SimSun', serif">宋体</option>
              <option value="'SimHei', sans-serif">黑体</option>
              <option value="'KaiTi', serif">楷体</option>
              <option value="'FangSong', serif">仿宋</option>
              <option value="'Times New Roman', serif">Times New Roman</option>
              <option value="Georgia, serif">Georgia</option>
              <option value="'Courier New', monospace">Courier New</option>
            </Select>
          </FormGroup>
        </SettingsSection>
        
        <SettingsSection>
          <SectionTitle>
            <HomeIcon />
            启动界面
          </SectionTitle>
          
          <FormGroup>
            <Label>启动时显示</Label>
            <Select 
              value={settings.startupModuleType} 
              onChange={(e) => handleSettingChange('startupModuleType', e.target.value)}
            >
              <option value="last">继承上次界面</option>
              <option value="specific">指定界面</option>
            </Select>
          </FormGroup>
          
          {settings.startupModuleType === 'specific' && (
            <FormGroup>
              <Label>指定界面</Label>
              <Select 
                value={settings.startupModule} 
                onChange={(e) => handleSettingChange('startupModule', e.target.value)}
              >
                <option value="calendar">日历</option>
                <option value="bookmark">书签</option>
                <option value="todo">待办</option>
                <option value="note">笔记</option>
                <option value="quote">一语</option>
                <option value="news">新闻</option>
              </Select>
            </FormGroup>
          )}
        </SettingsSection>
        
        <SettingsSection>
          <SectionTitle>
            <BackupIcon />
            备份与恢复
          </SectionTitle>
          
          <ButtonGroup>
            <Button className="primary" onClick={exportSettings}>
              <BackupIcon />
              导出设置
            </Button>
            
            <FileInputLabel>
              <RestoreIcon />
              导入设置
              <FileInput 
                type="file" 
                accept=".json" 
                ref={importRef}
                onChange={importSettings}
              />
            </FileInputLabel>
          </ButtonGroup>
        </SettingsSection>
      </SettingsContent>
    </SettingsContainer>
  );
};

export default SettingsPanel;
